css:
@-moz-document url-prefix("https://moodle.ktu.edu/") {
.jumbotron {
    background-color: black!important;
    background-image: none!important;
}
.frontpage .jumbotron .texts b {
    color: white!important;
}
.frontpage .row-fluid {
    background-color: black!important;
    background-image: none!important;
}

.card {
    background-color: #373534;
    color: white;
}
.card a {
    color: #85A7DC;
}
#page {
    background-color: black;
    color: white;
}
#region-main {
    background-color: #373534;
    color: white;
}
#region-main a {
    color: #85A7DC;
}
#region-main table {
    color: white!important;
}
#region-main table :hover {
    color: white!important;
}
.navbar {
    background-color: #373534!important;
    color: white;
}
.navbar a {
    color: #85A7DC!important;
}
.message-app .body-container {
    background-color: black!important;
    color: #EAEAEA;
}
.message-app .body-container .bg-white {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .body-container .list-group a {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .message {
    background-color: #787878!important;
    color: #EAEAEA;
}
.message-app .text-muted {
    color: #E0E0E0!important;
}
.message-app .footer-container .p-2 {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .footer-container textarea {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .footer-container .text-center {
    background-color: black!important;
    color: #EAEAEA;
}
.message-app .header-container .bg-white {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .header-container .border-bottom {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .panel-header-container {
    background-color: #373534!important;
    color: #EAEAEA;
}
.message-app .border-right {
    background-color: black!important;
    color: #EAEAEA;
}
.message-app .panel-body-container .list-group a {
    background-color: #373534!important;
    color: #EAEAEA;
}

#nav-drawer {
    background-color: black;
    color: white;
}
#nav-drawer li a {
    background-color: #373534;
    color: white;
}
#nav-drawer .list-group-item {
    background-color: #373534;
    color: white;
}
.popover-region-container {
    background-color: #373534!important;
}
.preferences-container .preference-table tr td:nth-child(even) {
    background-color: #242424!important;
}
.preferences-container .preference-table tr th:nth-child(even) {
    background-color: #242424!important;
}
.generaltable td {
    background-color: black!important;
}
.generaltable th {
    background-color: #242424!important;
}
.info {
    background-color: #242424!important;
}
.btn {
    border-color: transparent!important;
}
.submissionnotgraded {
    color: white!important;
}
.submissionstatussubmitted {
    color: white!important;
}
}
