css:
@-moz-document domain("nauka.rocks") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #a358ba #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #a358ba !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="tel"],input[type="url"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #a358ba !important;
}
input[type="submit"]:hover{
    background: #a358ba !important;
    color: #fff !important;
}
/*HEADER*/
.main-navigation, .main-navigation ul ul{
    background: #a358ba;
}
.main-navigation a, .menu-toggle{
    color: #1c1c1c !important;
}
.main-navigation .main-nav ul li:hover > a{
    background: #1c1c1c;
    color: #a358ba !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
body.home,#category-section .posts .single-post, #category-section .pis-li,section#popularne-teraz li{
    background: #1c1c1c;
}
.black-titles a.pis-title-link, .black-titles .pis-categories-links a, .black-titles .pis-utility a{
    color: #a358ba !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
aside.posts-in-sidebar li{
    background: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
.main-navigation .main-nav ul li[class*="current-menu-"] > a{
    background: #1c1c1c;
    color: #a358ba !important;
}
.main-navigation .main-nav ul li[class*="current-menu-"] > a:hover,.inside-article{
    background: #1c1c1c;
}
.taxonomy-description{
    background: #1c1c1c;
    color: #a358ba !important;
    border: 1px solid #a358ba;
}
a.button{
    color: #1c1c1c !important;
}
.navigation-search input{
    opacity: 1;
}
/*PODSTRONY*/
/*FOOTER*/
.copyright-bar:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #a358ba;
    cursor: none;
}
/*FOOTER*/
}
