css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.satchelone.com"), domain("www.showmyhomework.co.uk"){
/*
    Thanks for looking at my satchel one CSS theme, variables at the top are for easy accent colour change, the image URL is commented so you can easily find it, I hope you have fun. This is very hacked together but,
    I may eventually get round to making it clean and to removing redundant lines and classes, there are probably quite a few in there.
*/
:root {

    --accent-colour: /*[[accent]]*/;
    --accent-transparency-colur: rgba(/*[[accent-rgb]]*/, 0.73);
    --accent-shade-colour: /*[[sAccent]]*/;

    /*Following are darkening layer colours, shouldn't be darkened unless necessary*/
    --tShade-1: #736f6d54;
    --tShade-2: #31303057;
    --tShade-3: #736f6da8;
    --tShade-4: #282524a8;
    --tShade-5: #82828299;
}

.img-thumbnail,
body {
    /* URL IMAGE LINE HERE:  */
    background-image: url('/*[[image]]*/');
    background-attachment: fixed;
    background-repeat: no-repeat;
    height: auto;
    background-size: cover;
    color: #ffff;
}
div.counter {
    color: var(--accent-colour);
}
div.margin-top div.row {
    background-color: transparent;
}
div.margin-top {
    background-color: var(--tShade-1);
}
.__filter-bar__7a335 {
    background-color: transparent;
    border: 0;
}
.satchel-color-bar {
    display: none;
}
div.col-md-14 {
    width: 100%;
    margin: 2.5% 0 2.5%;
}
.table-striped {
    background-color: var(--tShade-1);
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: var(--tShade-4);
}
table.table {
    color: var(--accent-colour)
}
table.table a {
    color: white;
}
.main-content .table th {
    color: var(--accent-colour)
}
div.todo-divider.hr.past-due:after {
    background-color: rgb(255, 255, 255);
    border-color: rgb(255, 255, 255);
    border-radius: .25rem;
}
div.todo-divider.hr.past-due {
    border-color: rgb(255, 255, 255);
}
#page-content-wrapper {
    background-color: var(--tShade-1);
    padding-top: 60px;
    border: 0;
}
#page-content-wrapper .main-content {
    border: 0;
}
.satchel-frame__wrapper {
    padding-top: 0;
}
div.row div div div div div div h4 a {
    color: var(--accent-colour)
}
div.row div div div div div div h4 a:active {
    color: var(--accent-shade-colour)
}
div.row div div div div div div h4 a:hover {
    color: var(--accent-shade-colour)
}

.col-md-3.date {
    color: #ffff;
}
div.row div div div div {
    /*#82828247 base background color*/
    background-color: var(--tShade-5);
    border-radius: .25rem;
}
div.row div div div div div {
    background-color: transparent;
}
div.row {
    background-color: transparent;
}
div.satchel-top-bar {
    background-color: transparent;
    margin-left: 0;
    border: 0;
}
div.well.homework-information {
    background-color: var(--tShade-3);
}
.sidebar-menu__item {
    background-color: var(--tShade-3);
}
div.satchel-menu {
    background-color: var(--tShade-3);
    /*
    background-image: url('https://raw.githubusercontent.com/WaifuWarrior/cssTheme/master/pattern.png');
    */
}
div.satchel-menu__brand.right-part {
    background-color: transparent;
    padding-top: 25px;
}
div.satchel-menu__brand.right-part svg {
    height: 0;
    padding: 0;
    position: fixed;
    margin-top: 60px;
    display: none;
    float: left;
}
div.satchel-top-bar {
    margin-left: 0;
}
.linearicon-options {
    z-index: 3;
}
div.satchel-top-bar__content {
    border: 0;
    background-color: transparent;
    z-index: 1;
}
.satchel-top-bar__content--minimized {
    margin-left: 63px;
}

header.main-header,
div.tab-container.tabs-extended {
    background-color: transparent;
    border: 0;
}
.div.container-fluid.main-content {
    border: 0;
}
.tab-container ul.tabs li a.active,
.tab-container ul.tabs li a:focus,
.tab-container ul.tabs li a:hover {
    background-color: transparent;
}

.satchel-menu.satchel-menu--size-medium {
    background-color: transparent;
    /*
    background-image: url('https://raw.githubusercontent.com/WaifuWarrior/cssTheme/master/pattern.png');
    background-image: url("https://raw.githubusercontent.com/WaifuWarrior/cssTheme/master/pattern.png"), linear-gradient(#eb01a5, #d13531);
    background-size: 2%;
    */
    background: linear-gradient(to right, var(--accent-colour), var(--tShade-1));
    width: 240px;
    margin-top: 0;
    z-index: 2;
}
.satchel-menu.satchel-menu--minimized {
    background-color: transparent;
    /*
    background-image: url('https://raw.githubusercontent.com/WaifuWarrior/cssTheme/master/pattern.png');
    background-size: 25%;
    
    */
    background: linear-gradient(to right, var(--accent-colour), var(--tShade-1));
    margin-top: 0;
    width: 63px;
    z-index: 2;
}

.satchel-menu {
    padding-top: 50px;
    z-index: 3;
}
.satchel-gradient--30deg,
.sidebar-menu__item {
    background-color: transparent;
    background: none;
    color: #fefeff;
}
.event-row,
.smhw-dropdown-menu {
    color: var(--accent-colour);
}
.sidebar-menu__item:hover,
.sidebar-menu__item:focus,
.sidebar-menu__item:active,
.sidebar-menu__item.active {
    background-color: var(--accent-transparency-colur);
    border-left-color: var(--accent-shade-colour);
}

.__attachment-link__42355 .attachment .file-type {
    color: #fff;
    background-color: var(--accent-colour);
}
.__attachment-link__42355 .attachment a,
.__attachment-link__42355 .attachment .filename {
    color: #fff;
}
.tab-container.tabs-extended ul.tabs li a {
    color: var(--accent-colour);
}
.student-submission-box {
    background-color: var(--tShade-1);
}
div.activity-feed-entry.activity-feed-entry--form div {
    background-color: transparent;
}
div.activity-feed-entry div.activity-feed-entry-content {
    background-color: #ffff;
    color: var(--accent-colour);
}
.col-xs-16 {
    background-color: var(--tShade-1);
}
.btn-default {
    background-color: var(--accent-colour);
    border-color: var(--accent-shade-colour);
    color: white;
}
.btn-default:active,
.btn-default:hover,
.btn-default:focus {
    background-color: var(--accent-shade-colour);
    border-color: var(--accent-colour);
    color: white;
}
.__assignment-actions__bca89 .smhw-dropdown-btn {
    background-color: var(--accent-colour);
}
.__main-header__a7ee6 h1 {
    color: #ffff;
    padding: 3px 4px;
    background-color: var(--tShade-1);
    border-radius: .25rem;
}
.assignment-description-title strong {
    color: var(--accent-colour);
}
.btn-primary {
    color: #fff;
    background-color: var(--accent-colour);
    border-color: var(--accent-shade-colour);
}
.btn-primary:active,
.btn-primary:hover,
.btn-primary:focus {
    border-color: var(--accent-colour);
    background-color: var(--accent-shade-colour);
}
.inline-date-picker .ember-power-datepicker-trigger .prepended-icon {
    color: var(--accent-colour);
}
.notice-board .calendar-nav-buttons .btn-default {
    margin-left: 0px;
}
.legend-list-dropdown__inner {
    color: black;
}
.smhw-dropdown-menu .list-item__trigger {
    color: var(--accent-colour);
}
.school-calendar-wrapper__calendar-legend {
    margin-bottom: 20px;
}
.__legend-list-dropdown__17616 .legend-list-dropdown__toggle {
    color: var(--accent-colour);
}
.pagination > li > a,
.pagination > li > span {
    color: var(--accent-colour);
}
.pagination > li > a {
    color: var(--accent-colour);
}
.pagination > li > a:hover {
    color: var(--accent-shade-colour);
}
.pagination > .active > a,
.pagination > .active > a:focus,
.pagination > .active > a:hover {
    color: var(--accent-colour);
    background-color: var(--accent-shade-colour);
    border-color: var(--accent-colour);
}
a {
    color: var(--accent-colour);
}
a:hover,
a:focus,
a:active {
    color: var(--accent-shade-colour);
}
.btn-group .btn-group-item.active {
    color: white;
    background-color: var(--accent-shade-colour);
}
.breadcrumb a:not(.brand-primary-text),
.breadcrumb {
    color: var(--accent-colour);
}
.__student-gradebook__submission-thumbnail__651ad .fake-cover .footer,
.__student-gradebook__submission-thumbnail__651ad .fake-cover {

    background-color: var(--tShade-1);
    box-shadow: none;
}
.__student-gradebook__submission-thumbnail__651ad .fake-cover .info h3 {
    color: white;
}
}
