css:
@-moz-document domain("dyjix.eu") {
/* MAIN START */
body {
    background: #2b2b2b;
    color: #b3b3b3;
    font-family: open sans, sans-serif !important;
    font-size: 14px;
    font-style: normal;
    font-weight: 400;
    margin: 0;
    position: relative;
}

.topminibar {
    border-bottom: 1px solid rgb(47, 51, 54);
}

body,
button,
input,
select,
textarea {
    font-family: "Open Sans", Verdana, Tahoma, serif;
    color: #b3b3b3;
}

.top {
    background: rgb(32, 35, 39);
    position: relative;
}

.sf-menu li a {
    color: white;
    font-weight: 400;
    padding: 30px 14px;
    text-transform: capitalize;
}

.bg_gray {
    background: #3d3d3d !important;
}

.pricingtables .panel-info {
    border: 0;
    box-shadow: 1px 9px 9px -6px #595959;
    box-shadow: 2px 9px 14px -2px #595959;
    -webkit-box-shadow: 2px 9px 14px -2px #595959;
    -moz-box-shadow: 2px 9px 14px -2px #595959;
    -o-box-shadow: 2px 9px 14px -2px #595959;
}

.panel {
    margin-bottom: 20px;
    background-color: #242424;
    border: 1px solid transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
}

.sf-menu li:hover a,
.sf-menu li.sfHover a,
.sf-menu li.current a {
    color: #bfbfbf;
    -webkit-transition: none;
    transition: none;
    font-weight: 400
}

.sf-menu ul li a {
    background: rgb(32, 35, 39) !important;
    font-size: 13px;
    font-weight: 300;
    padding: 10px 15px;
    border-bottom: 1px solid rgb(47, 51, 54);
}

.pricingtables ul li:nth-child(2n+1) {
    background: #242424;
}

.pricingtables .panel-footer {
    text-align: center;
    background-color: #1c1c1c;
}

.panel-footer {
    padding: 10px 15px;
    background-color: #1c1c1c;
    border-top: 1px solid rgb(47, 51, 54);
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #ff5e3a;
    line-height: 1.1;
    margin-bottom: 12px;
    margin-top: 12px;
    text-rendering: optimizelegibility;
    text-align: center;
}

.r-tabs .r-tabs-nav .r-tabs-tab:first-child {
    border-left: 1px solid rgba(0, 0, 0, 0);
}

.r-tabs .r-tabs-nav .r-tabs-anchor {
    color: white;
    float: left;
    font-weight: 400;
    margin: 0;
    padding: 20px 50px;
    text-decoration: none;
}

.r-tabs .r-tabs-nav .r-tabs-tab {
    background: #303030;
    border-right: 1px solid rgb(47, 51, 54);
    margin: 0;
    position: relative;
}

.copyright {
    padding: 10px;
    background: #1c1c1c;
    font-size: 13px;
    text-align: center;
    border-top: 1px solid rgb(47, 51, 54);
}

.extra-features .facility {
    border: 1px solid #1b1b1b;
    border-top: 6px solid #1b1b1b;
    position: relative;
    padding-bottom: 30px;
    background: #333;
    -webkit-box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .16);
    -moz-box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .16);
    box-shadow: 0 2px 10px 0 rgba(0, 0, 0, .16);
}

/* MAIN END */

/* START CLIENT AREA */
.panel-sidebar > .panel-footer {
    color: #333;
    background-color: #1b1b1b;
    border-top: 0;
}

.tiles .tile {
    padding: 12px 20px;
    background-color: rgba(0, 0, 0, 0);
    border-right: 1px solid #ccc;
}

.client-home-panels .panel > .panel-heading {

    background-color: #1b1b1b;
    border-bottom: 0;
    font-weight: 700;
}
.panel-default > .panel-heading {

    color: white;
    background-color: #f5f5f5;
    border-color: #ddd;
    border-bottom-color: rgb(221, 221, 221);
}

table.table-list thead th {
    padding: 8px;
    padding-right: 8px;
    padding-left: 8px;
    background-color: #1b1b1b;
    border-bottom: 1px solid #333;
    color: white;
    text-align: center;
}

.dataTables_wrapper table.table-list thead th.sorting_asc,
.dataTables_wrapper table.table-list thead th.sorting_desc {
    background-color: rgba(0, 0, 0, 0);
}

.table-list > tbody > tr > td {
    background-color: #333;
}

.table-list > tbody > tr:nth-child(2n) > td {
    background-color: #1b1b1b;
}

table.table-list tbody td {
    padding: 10px;
    vertical-align: middle;
    font-size: .94em;
}
.table > tbody > tr > td,
.table > tbody > tr > th,
.table > tfoot > tr > td,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > thead > tr > th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 1px solid rgb(47, 51, 54);
}

.dataTables_wrapper .dataTables_length {
    background-color: #1a1a1a;
    font-size: .8em;
    color: #888;
}

.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #1b1b1b;
}

div.product-details div.product-icon {
    margin: 0;
    padding: 0;
    background-color: #1b1b1b;
}

.fa-inverse {
    color: #333;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
    color: #555;
    cursor: default;
    background-color: #333;
    border: 1px solid rgb(47, 51, 54);
    border-bottom-color: rgb(47, 51, 54);
    border-bottom-color: transparent;
}

.panel-default > .panel-heading {
    color: white;
    background-color: #333;
    border-color: #ddd;
    border-bottom-color: rgb(47, 51, 54);
}

/* END ClIENT AREA */

/* START PANEL */
section#main-body {
    margin: 0;
    padding: 20px 0;
    min-height: 350px;
    background-color: rgb(32, 35, 39);
}

body, button, input, select, textarea {
    color: black;
}

.home-kb-search .form-control {
    padding-left: 50px;
    background-color: #1b1b1b;
    border: 1px solid #333;
    color: #999;
    font-weight: 300;
}

body,
button,
input,
select,
textarea {
    font-family: "Open Sans", Verdana, Tahoma, serif;
    color: #FFF;
}

section#header {
    margin: 0;
    padding: 10px 0;
    background-color: #2b2b2b;
    border-radius: 5px 5px 0 0;
}

ul.top-nav > li > a {
    padding: 6px 10px;
    color: white;
    line-height: 20px;
    text-decoration: none;
}

ul.top-nav > li:active > a,
ul.top-nav > li:focus > a,
ul.top-nav > li:hover > a,
ul.top-nav > li > a:focus {
    color: #5c5c5c;
    text-decoration: none;
    background: 0 0
}

section#footer .back-to-top i {
    padding: 10px;
    background-color: #1f1e1e;
    color: black;
    border-radius: 3px;
    opacity: .7;
}

#order-slider .prodcats div {
    margin: 10px;
    background-color: #c4c4c4;
    border: 1px solid rgb(47, 51, 54);
    font-size: 1.5em;
    text-align: center;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    -o-border-radius: 5px;
    border-radius: 5px;
}

section#footer {
    margin: 0;
    padding: 20px 10px;
    background-color: #2b2b2b;
    border-top: 1px solid rgb(47, 51, 54);
    color: white;
    font-size: .9em;
}

#order-slider .cartslider {
    background-color: #1b1b1b;
    border: 1px solid rgb(47, 51, 54);
    border-bottom-color: rgb(47, 51, 54);
    border-bottom-style: solid;
}

.sliderlabel {
    color: white;
}

#order-slider .cartprods {
    background-color: #333;
    border: 1px solid rgb(47, 51, 54);
}

#order-slider .ordernow {
    margin: 5px;
    padding: 6px 10px;
    background-color: #7bb928;
    border: 1px solid #aadd64;
    color: white;
}

#order-slider table.cart td {
    background-color: #333;
    border-bottom: 1px solid rgb(47, 51, 54);
}

#order-slider table.cart tr.subtotal td {
    background-color: #262626;
    border-bottom: 1px solid rgb(47, 51, 54);
    font-size: 12px;
    padding: 4px;
}

#order-slider table.renewals tr:hover td {
    background-color: #757575
}

#order-slider table.cart tr:hover td {
    background-color: #919191;
}

#order-slider table.cart tr.subtotal:hover td {
    background-color: #757575;
}


.panel-sidebar > .panel-heading {
    color: white;
    background-color: #1b1b1b;
    border-bottom: 0;
}

.panel-sidebar .panel-title {
    color: white;
}

.list-group-item {
    border-color: rgb(47, 51, 54);
}

.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: #333;
    border: 1px solid rgb(47, 51, 54);
}

/* TWITTER */
#twitterFeedOutput {
    width: 100%;
    height: 60%;
    border-style: solid;
    border-radius: 10px;
    border-color: rgb(47, 51, 54);
}

#twitterFeedOutput p {
    text-align: center;
    margin-top: 10px;
}

.tweet {
    width: 90%;
    margin-left: 5%;
    margin-top: 1%;
    border-style: none none dotted none;
    border-width: 2px;
    border-color: rgb(47, 51, 54);
}

/* END PANEL */

/* FACTURE */
.invoice-container td.total-row {
    background-color: #1c1c1c;
}

.invoice-container {
    background-color: #3d3d3d;
    border: 1px solid #3d3d3d;
}
}
