css:
@-moz-document domain("portal.lanis-system.de") {
body {
    color: #fff;
}
.accordion-toggle {
    color:#fff!important;
    cursor:default;
    font-weight:700;
}
    
.box {
    background-color:#264a7c!important;
    border:none!important;
}
    
.box,.panel {
    box-shadow:none!important;
}
    
.container {
    left:0;
    right:0;
}
    
.masthead,td a img,.preshow,.btn.btn-default.pull-right,.row.clearfix.hidden-print {
    display:none!important;
}
    
.panel {
    background-color:rgba(0,0,0,0)!important;
    border:none!important;
    border-radius:0!important;
    padding:12px!important;
}
    
.panel-heading {
    background-color:inherit!important;
}
    
.schoolbg,body,tr {
    background-color:#202833!important;
    background-image:none!important;
    opacity:100!important;
}
    
.textheight,.hidden-print,.container visible-lg visible-md {
    background-color:#202833!important;
    border:none!important;
}
/*kursmappe*/
.thumbnail, .col-md-4 {
 
    background-color:#202833;
    border:none;
    border-radius:0;
    height: 250px !important;

}

#mappen h2 {
    padding-bottom: 30px !important;
}



.toolbarsticky {
    height:0!important;
    margin:0!important;
    padding:0!important;
    width:0!important;
}
    
a  {
    color:#5bc0de;
}

.btn.btn-primary.dropdown-toggle.btn-xs {
    background-color: #54acc7;
}

.btn.btn-primary.dropdown-toggle.btn-xs:hover {
    background-color: #5bc0de;
}

td h3 a:hover {
    color: #54acc7;
}

div.th-inner.sortable.both {
    background-color:#202833;
}
    
h1,h2,h3 {
    color:#fff!important;
}
    
td,nav {
    border:none!important;
}
    
tr {
    color:#fff;
    padding: 20px !important;
    margin: 50px 50px !important;
}

.pull-left.pagination-detail {
    color: white;
}

ul.nav.nav-tabs li.active a {
    background-color: #202833 !important;
    color: #fff !important;
}

ul.nav.nav-tabs li a {
    background-color: #202833 !important;
    color: #5bc0de;
}

.markup.text.realHomework {
    border-radius: 0 !important;
}

.alert.alert-info {
    background-color: #202833 !important;
}

.label {
    border: none !important;
    border-top-left-radius: 2px !important;
    border-top-right-radius: 2px !important;
}

.label-warning {
    background-color: #d9534f !important;
    border: none !important;
}

button.btn.btn-warning.btn-sm.dropdown-toggle {
    background-color: #d9534f;
    border:none !important;
}

.btn-info {
    background-color: #337ab7;
    border:none !important;
}

.btn-default {
    color: white !important;
    background-color: #777777 !important;
    border: none !important;
}

#content div div ul li .message.you-message a {
    background-color: unset !important;
    color: #5bc0de;
}

.message.you-message{
    background-color: #264a7c !important;
    border: none !important;
    border-radius: 2px !important;
    color: #fff !important;
}

.message.you-message::after {
    border-color: #264a7c transparent !important;
        border-top-color: #264a7c;
        border-right-color: transparent;
        border-bottom-color: #264a7c;
        border-left-color: transparent;
}

.dropdown-menu {
    background-color: #202833;
}

.dropdown-menu a:hover {
    background-color: #264a7c !important;
}
.dropdown-menu a{
    color: white !important;
}

.navbar-custom .navbar-nav > .open > a, .navbar-custom .navbar-nav > .open > a:hover, .navbar-custom .navbar-nav > .open > a:focus {
    background-color: #202833 !important;
}

[data-id="Allgemein"] {
    display: none !important;
}

/*stundenplan*/
.stunde {
    background-color: #202833 !important;
    color: #fff;
}
.hover-warning {
    background-color: #2e3949 !important;
}
.alert-warning {
    color: white;
    background-color: #d9534f;
    border: none !important;
}

/* Login */
.btn.btn-warning.btn-sm, .btn.btn-default.btn-sm.backbtn {
    box-shadow: none !important;
}

#alllogin {
    Background-color:#5cb85c !important;
}
}
