css:
@-moz-document url-prefix("https://thepiratebay.org/") {
#browse, body {
    background: #1c1c1ce6;
    font-size: 16px;
}

#browse, a, a:link, a:visited, a:focus {
    color: #ccc;
    border-bottom: none;
}

a:hover, a:focus {
    color: #fff !important;
    text-decoration: none;
    text-outline: none;
    border-bottom: none;
}

#logo {
    display: none;
}

#torrents span.list-header {
    border: none;
}

#torrents li.list-entry span.item-type {
    text-align: left;
}

.list-item.item-name.item-title {
    font-weight: 500;
}

.list-item, #torrents span.list-header, #torrents {
    background: #272727;
}

#torrents span.list-header {
    height: 5vh;
    font-size: 1.5em;
    font-family: 'Arial', 'Helvetica';
}

#torrents span.list-header label {
    margin: auto;
}

.list-entry {
    height: 2vh;
}

footer {
    display: none !important;
}

#torrents li.list-entry, #torrents li.list-entry.alt {
    background: inherit !important;
}

.list-item.list-header.item-user, .list-item.item-user {
    display: none !important;
}

.list-header label {
    cursor: pointer
}

#home input[type="search"] {
    height: 5vh;
    width: 450px !important;
    background: #eee;
    border: none;
    color: #141414;
}

input[type="submit"] {
    height: 4.5vh;
    background: #ff9800;
    border: none;
    color: #000;
    border-radius: 2px;
    cursor: pointer;
    padding: 0 20px;
}

input[type="submit"]:hover {
    background: #ec6c0aeb;
    color: black;
}

.form-box {
    color: #ccc;
    user-select: none;
}

header form input, header form select {
    height: 4.5vh;
}

#description_container {
    background: #272727;
    padding-bottom: 0;
}

#description_container .text-box {
    background: #363636;
    border: none;
    width: 100%;
    margin: 0;
}

.links, h1 {
    display: none;
}

#description_container h2 {
    height: 5vh;
    color: black;
    background: rgb(255, 152, 0);
    background: linear-gradient(180deg, rgba(255, 152, 0, 1) 0%, rgb(187, 112, 1) 100%);
    -webkit-box-shadow: 7px 11px 6px -3px rgba(0, 0, 0, 0.75);
    -moz-box-shadow: 7px 11px 6px -3px rgba(0, 0, 0, 0.75);
    box-shadow: 7px 11px 6px -3px rgba(0, 0, 0, 0.75);
    text-align: center;
    border: none;
    margin-bottom: 2vh;
}

li.alt {
    background: unset !important;
}
}

@-moz-document url-prefix("https://thepiratebay.org/") {
@media screen and (prefers-color-scheme: light) {
    #browse, body {
        background: #fff;
        font-size: 16px;
        color: black;
    }

    .list-item, #torrents span.list-header, #torrents {
        background: transparent;
    }

    main {
        background: #f4f4f4;
        -webkit-box-shadow: 9px 10px 5px -6px rgba(135, 135, 135, 1);
        -moz-box-shadow: 9px 10px 5px -6px rgba(135, 135, 135, 1);
        box-shadow: 9px 10px 5px -6px rgba(135, 135, 135, 1);
        margin-bottom: 3vh;
    }

    #browse, a, a:link, a:visited, a:focus {
        color: #171717de;
    }

    a:hover, a:focus {
        color: black !important;
    }

    #description_container .text-box, #description_container {
        background: none;
    }

    #description_container h2 {
        box-shadow: 7px 9px 7px -3px rgba(123, 123, 123, 0.75);
    }

    #torrents span.list-header {
        border-bottom: solid 1px #807f7f;
    }

    .form-box {
        color: #2d2d2d;
    }
}
}

@-moz-document url("https://thepiratebay.org/index.html") {
@media (prefers-color-scheme: dark) {
    img {
        background: url("https://svgur.com/i/LNs.svg");
        height: 0;
        padding: 160px;
    }
}
}

@-moz-document url-prefix("https://thepiratebay.org/index.html") {
@media screen and (prefers-color-scheme: light) {
    main {
        background: transparent;
        box-shadow: none;
    }

    #home input[type="search"] {
        box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08);
        background: none;
    }
}
}
