css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linkedin.com") {

/* Home */
#content a:visited { color: #039 !important; }
#main a:visited { color: #039 !important; }
div.notice a { color: #039 !important; }
#content #home-inbox h2 a { color: #000 !important; }
#content #home-inbox h2 span a { color: #039 !important; }
#content div.feed h2 a { color: #000 !important; }
#content h2.new a { color: #000 !important; }
#pymk a { color: #039 !important; }
#wvmp div.content p a { color: #039 !important; }


/* Address Book */
#abook-group a:visited { color: #039 !important; }

/* Search */
#result-set h2 a:visited, #content p.page a:visited { color: #7495C6 !important; }
#content p.page a:visited strong { color: #039 !important; } 

/* Jobs */
#resultspg table.jobsearch td.jobtitle strong a:visited { color: #7495C6 !important; }

/* Companies */
#main table.company-results td.company-name a:visited { color: #7495C6 !important; }

/* Groups */
#main ul.groupdir h3 a:visited { color: #7495C6 !important; }
#main div.contain p.paginate a:visited { color: #7495C6 !important; }
#main div.contain p.paginate a:visited strong { color: #039 !important; } 

/* Company Group */
#news ul.articles a:visited { color: #7495C6 !important; }
#news ul.articles p.add-article a, #news ul.articles p.more-news a { color: #039 !important; }
#anet-answers div.content h3 a:visited { color: #7495C6 !important; }
#q-list h3 a:visited { color: #7495C6 !important; }

#nav-personal ul a { color: #006699 !important; }
#profile-snapshot a.fn { color: #000 !important; }
#footer a { color: #006699 !important; }

/* Reset button */
#main p.btn a { color: #000 !important; }

}
