css:
@-moz-document domain("kukulturze.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #303bcc #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #303bcc !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="tel"],input[type="url"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #303bcc !important;
}
input[type="submit"]:hover{
    background: #303bcc !important;
    color: #fff !important;
}
/*HEADER*/
.mh-header,.mh-subheader{
    background: #1c1c1c;
}
.custom-logo{
    border: 4px solid #303bcc;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.mh-wrapper,#wpcomm .wpdiscuz-front-actions,#wpcomm .wc-comment .wc-comment-right{
    background: #1c1c1c;
}
#wpcomm .wc-vote-link.wc-up{
    color: green !important;
}
#wpcomm .wc-vote-link.wc-down{
    color: red !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
figcaption{
    color: #fff !important;
}
