css:
@-moz-document domain("forum.sc2.zone") {
@font-face {
	font-family:'Roboto';
	font-style: normal;
	font-weight: 400;
	src: url('https://fonts.googleapis.com/css2?family=Roboto&display=swap');
}
body {
	background-color:#32353B;
	font-family:'Roboto', sans-serif;
	letter-spacing: 0.03em;
}
.DiscussionPage-list {
	background-color:#32353B;
}
.Button--primary {
	color: white;
}
.sideNav .Dropdown--select .Dropdown-menu>li>a {
	color: white;
}
.DiscussionList:not(.DiscussionList--searchResults) .read .DiscussionListItem-title {
	color: #A4A4A5;
	font-weight: normal;
}
.DiscussionList:not(.DiscussionList--searchResults) .unread .DiscussionListItem-title {
	color: white;
	font-weight: 500;
}
.DiscussionListItem-info {
	color: #77828E
}
/*
 *  scrollbars
 */
*::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
	background-color: #32353B;
}
*::-webkit-scrollbar {
	width: 10px;
	background-color: #F5F5F5;
	padding-top:5px;
	padding-bottom:5px;
}
*::-webkit-scrollbar-thumb {
	border-radius: 10px;
	background-color: #0ae;
	background-image: -webkit-gradient(linear, 0 0, 0 100%, color-stop(.5, rgba(255, 255, 255, .2)), color-stop(.5, transparent), to(transparent));
}
}
