css:
@-moz-document url-prefix("https://avenoel.org/") {
@font-face {
  font-family: 'Roboto Condensed';
  font-style: normal;
  font-weight: 700;
  font-display: swap;
  src: local('Roboto Condensed Bold'), local('RobotoCondensed-Bold'), url(https://fonts.gstatic.com/s/robotocondensed/v18/ieVi2ZhZI2eCN5jzbjEETS9weq8-32meGCQYbw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

.main-section {
    padding: 2.5rem 2.25rem !important;
    border-radius: 0px !important;
}

.topics-title > a {
    color:#006bd7;
    font-family: Tahoma,DejaVu Sans Condensed,Arial,Helvetica,sans-serif;
    font-weight: bold;
    display: flex;
    text-decoration: none !important;
}

.topics-title > a:visited {
    color:#777;
}

.table-striped > tbody > tr {
    background-color: #fff;
    cursor: pointer;
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #eef5fc;
}

.table-striped > tbody > tr:hover {
    background-color: #f7f7e6;
}

.table-striped > tbody > tr:hover > .topics-title > a:not(:visited) {
    color: #f79726;
    text-decoration: none;
}

.topic-icon > a {
    position: relative;
    top: 3px;
}

.topic-icon > a > img {
    box-sizing: border-box;
    padding-left: 16px;
    width: 16px;
    height: 16px;
}

.topic-icon {
    width: 27px;
}

.topic-icon > a > img[alt="Topic topic jaune"]{
    background: url(https://www.jeuxvideo.com/img/forums/topic-dossier1.png) no-repeat;
    background-size: cover;
}

.topic-icon > a > img[alt="Topic topic rouge"]{
    background: url(https://www.jeuxvideo.com/img/forums/topic-dossier2.png) no-repeat;
    background-size: cover;
}

.topic-icon > a > img[alt="Topic épinglé"]{
    background: url(https://www.jeuxvideo.com/img/forums/topic-marque-on.png) no-repeat;
    background-size: cover;
}

.topic-icon > a > img[alt="Topic épinglé et fermé"]{
    background: url(http://www.jeuxvideo.com/img/forums/topic-marque-off.png) no-repeat;
    background-size: cover;
}

.post-content {
    border-radius: 0px;
    resize: vertical;
    border: 1px solid #d8d8d6 !important;
    box-shadow: none !important;
}

.message-wrapper {
    padding: 12px !important;
}

.topic-message {
    border-radius: 0px;
    padding: 0px;
    background-color: white;
    border: 1px solid #d5d5d5;
    margin-bottom: 15px;
}

.topic-message:nth-child(odd){
    background: #eef5fc !important;
    border: 1px solid #bcd2e9;
}

.message-infos {
    display: none;
}

.message-avatar {
    position: absolute;
    width: 40px;
    height: 40px;
    border: 1px solid white;
    outline: 1px solid #d5d5d5;
    padding: 0px !important;
    margin-top: 12px;
    margin-left: 12px;
}

.message-avatar > img {
	object-fit: cover;
}

.message-aside {
    width: 0px !important;
    height: 0px !important;
    padding: 0px !important;
}

.message-username {
    left: 52px;
    bottom: 6px;
}

.message-username > a {
    font-family: 'Roboto Condensed', sans-serif;
    font-size: 19px !important;
}

.message-date {
    position: relative;
    left: 52px;
    bottom: 6px;
}

.message-date > a {
    color: #006bd7;
}

.message-header {
    padding-bottom: 52px !important;
    margin-bottom: 12px;
    border-bottom: 1px dotted #c6c6d0 !important;
}

.message-content {
    min-height: 0px !important;
    padding: 0px !important;
}

.message-footer {
    display: none;
}
}
