css:
@-moz-document domain("redcafe.net"){

body{
background: #e4e3c4 url(http://img132.imageshack.us/img132/164/pagebg.png) repeat-x top left !important;
}

.tcat{
background: #a62a0d url(http://img8.imageshack.us/img8/73/catbg.gif) repeat-x top left !important;
color: #FFF788 !important;
font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

.tfoot{
background: #a62a0d url(http://img8.imageshack.us/img8/73/catbg.gif) repeat-x top left !important;
color: #FFF788 !important;
font: bold 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}

.thead
{
background: #282828 url(http://img8.imageshack.us/img8/7624/subcatbg.gif) repeat-x top left !important;
color: #ffffff !important;
}

.vbmenu_control
{
background: #282828 url(http://img8.imageshack.us/img8/7624/subcatbg.gif) repeat-x top left !important;
}

.page
{
background: #e4e3c4 url(http://img132.imageshack.us/img132/164/pagebg.png) repeat-x top left !important;
}

.alt2, .alt2Active
{
background: #f2f2f2 !important;
border-top: 1px solid #ffffff !important;
}

.alt1, .alt1Active
{
background: #fafafa !important;
}

.tborder
{
background: #e0e0e0 !important;
}

a:link, body_alink
{
    color: #2671ac !important;
}
a:visited, body_avisited
{
    color: #2671ac !important;
}

.vbmenu_control a:link, .vbmenu_control_alink
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
    color: #FFFFFF !important;
    text-decoration: underline !important;
}
.tcat a:link, .tcat_alink
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
    color: #FFFFFF !important;
    text-decoration: underline !important;
}

.thead a:link, .thead_alink
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.thead a:visited, .thead_avisited
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
    color: #FFFFFF !important;
    text-decoration: underline !important;
}

img[src$="http://images.redcafe.net/img/forum/redcafe.jpg"]{
height:80px!important;padding-left:400px!important;
background-image:url("http://img189.imageshack.us/img189/7158/redcafelogo.jpg")!important;width:0!important}

/*the color you want to select*/
*[bgcolor="#E1E1E2"]
/*The Color you want to change it to*/
{background-color: #f8f7e8 !important;}

.tfoot a:link, .tfoot_alink
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.tfoot a:visited, .tfoot_avisited
{
    color: #ffffff !important;
    text-decoration: none !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover
{
    color: #FFFFFF !important;
    text-decoration: underline !important;
}

img[src$="http://images.redcafe.net/images/statusicon/forum_new.gif"]{
height:41px!important;padding-left:41px!important;
background-image:url("http://img25.imageshack.us/img25/9076/forumnewg.gif")!important;width:0!important}

img[src$="http://images.redcafe.net/images/statusicon/forum_old.gif"]{
height:41px!important;padding-left:41px!important;
background-image:url("http://img25.imageshack.us/img25/7373/forumoldq.gif")!important;width:0!important}

img[src$="http://images.redcafe.net/images/buttons/lastpost.gif"]{
height:12px!important;padding-left:12px!important;
background-image:url("http://img189.imageshack.us/img189/4087/lastpost.gif")!important;width:0!important}

img[src$="http://images.redcafe.net/images/buttons/collapse_tcat.gif"]{
height:15px!important;padding-left:15px!important;
background-image:url("http://img3.imageshack.us/img3/2267/collapsetcatt.gif")!important;width:0!important}

.time
{
color: #a62a0d !important;
}

}
