css:
@-moz-document regexp("https://allegrolokalnie.pl/.*") {
body
{
    background: #222;
}
#masthead
{
    background: black;
}
.masthead__main-nav__toggler
{
    color: white;
}
#masthead-menu ul
{
    background: #c3c3c3;
}
.search-form
{
    background: #222;
    border: 1px solid #444;
}
input[class^="search-form"]
{
    color: white;
}
.react-autosuggest__suggestion
{
    background: #c3c3c3;
    border-top: 1px solid #888;
}
li[class$="highlighted"]
{
    background: #a3a3a3;
    border-top: 1px solid #888;
}

div[class^="offer-page"], h2
{
    background: black;
    color: white;
}

div[class^="categories-nav"]
{
    color: white;
}
.list-plain
{
    background: black;
}
.categories-nav__megamenu
{
    background: black;
}
.categories-nav__link
{
    color: #c3c3c3 !important;
}
}

@-moz-document url("https://allegrolokalnie.pl/") {
div[class^="offer-page"], h2
{
    background: black;
    color: white;
}

div[class^="categories-nav"]
{
    color: white;
}
.list-plain
{
    background: black;
}
.categories-nav__megamenu
{
    background: black;
}
.categories-nav__link
{
    color: #c3c3c3 !important;
}
.home-page-categories h1, .home-page-carousel h2, .header--decorative
{
    color: white;
}
.offer-slide, .offer-slide a
{
    background: black;
    color: white;
}
.slick-slider .offer-slider .slick-initialized
{
    background: white !important;
}

.info-box-good-place, .info-box 
{
    background-color: black;
    color: white;
}
.site-footer, h3
{
    background: black;
    color: white;
}
.colophone
{
    background-color: black;
    color: white;
}
}

@-moz-document regexp("http(s)?://allegrolokalnie.pl/(oferta).*") {
.category-breadcrumbs
{
    background: #222;
}
.category-breadcrumbs__name
{
    color: #4095c8;
}
.slick-dots, .photo-carousel--with-multiple-photos
{
    background: black !important;
}

div[class^="slick-slide"]
{
    background: black;
}

div[class^="offer-page"], h2
{
    background: black;
    color: white;
}
.offer-page__action-box-and-safe-transaction-info
{
    background: #222 !important;
}
.offer-action-box-header__title, .offer-action-box-details__group
{
    color: white;
}
.offer-page__offer-id, .offer-page__rules-violation-report
{
    background: #222 !important;
}
.offer-action-box-buttons-panel
{
    background: black;
}
.offer-page__seller-other-offers
{
    background: #222 !important;
}
.offer-card, .offer-card h3
{
    background: black;
    color: white;
}

.bidding-user-message
{
    background: #222;
    color: white;
    border: solid #444;
}
.bid-incrementer
{
    background: #444;
    box-shadow: 0.2rem 0 0.1rem 0 #888;
    color: #d0d0d0;
}
#offer-bidding
{
    background: #222;
    border: 1px solid #444;
    color: white;
}
}

@-moz-document regexp("http(s)?://allegrolokalnie.pl/(wiadomosci).*") {
.masthead
{
    background: black;
}
.threads-layout__left
{
    background: black;
}
.conversation-tile
{
    background: black;
    border-top: 2px solid #444;
}
.text-11.ellipsis
{
    color: #454545;
    font-weight: bold;
}
.text-12.f-no-shrink
{
    color: #555;
}
.ellipsis
{
    color: #888;
}
.fw-semi
{
    color:#666;
}
.p-l-1.ellipsis
{
    color:#666;
}
.chat-header
{
    background: black;
    box-shadow: 0 2px 2px 0 #888, 0 0 1px 0 #888;
}
.chat-header h1
{
    color: white;
}
.messages__container, .new-message-panel
{
    background: black;
}
.threads-input__box--with-submit
{
    background: #222;
    color: white;
    border: 1px solid #888;
}
.offer__floating-link.btn.btn-secondary.btn-white.btn-sm
{
    background:#222;
    box-shadow: 0 2px 1px 0 #444;
}
#offer-scrollable
{
    background: black;
}
.offer, .offer h1
{
    background: black;
    color: white;
}
.card.product-card
{
    background: black;
    box-shadow: 0 2px 2px 0 #888, 0 0 1px 0 #888;
}
.offer__footer
{
    background: black;
}
.message.message--theirs
{
    background: #777;
}
.message.message--mine
{
    background-color:#f7ae10db;
}
}

@-moz-document regexp("http(s)?://allegrolokalnie.pl/(konto/oferty).*") {
.side-nav__item
{
    background: black;
    color: #555;
}
.side-nav__section-header
{
    background: black;
    color: white;
}
.side-nav__section-header:nth-of-type(n+2) {
    box-shadow: 0 2px 5px 0 #888, inset 0 1px 0 0 #888;
}
.page-header, .page-header__page-title
{
    background: black;
    color: white;
}
.listing__body
{
    background: #111;
}
.side-nav__item.side-nav__item--active
{
    background: rgba(247, 174, 16, .49);
    border-right: 4px solid #f7ae10;
    color: #222;
}
.page-header.d-f.f-justify-between.f-align-center.flush--mobile.hidden--app-view.m-b-2--mobile
{
    border-bottom: 1px solid #888;
}
.card
{
    box-shadow: 0 1px 1px 0 #444, 0 0 1px 0 #444;
}
.card.offer-card, .offer-card__extended
{
    background: black;
}
.text-11.text-uppercase.fw-semi
{
    color: #888;
}
.offer-card__title
{
    color: white;
}
.pagination__pages input
{
    background: #222;
    color: white;
    border: 1px solid #888;
}
.pagination__pages label
{
    color: white;
}
}
