css:
@-moz-document url-prefix("https://www.uworld.com/ClientApp/V2/") {
.gray-bg {
    background-color: #273542;
}
.footer {
    background: #273542!important;
    border-top: 0px !important;
}
.navbar-static-top {
    background-color: #273542!important;
    color:#fffaf0;
}
.modal-header.has-warning {
    background-color: #b96913;
}
.modal-header.has-error {
    background-color: #a22a37;
}
.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open .dropdown-toggle.btn-default {
    background-color: #4d4d4d;
}
.modal-content {
    background-color: #394B68;
    color: #fffaf0;
}
.ibox-title, .ibox-content {
    background-color: #2F4050;
    color:#fffaf0;
    border-color: #2F4050;
}
.createTestQuestionBox {
    background-color: #273542;
    border: 0px solid #cccccc;
    color: #f8f4eb!important;
    width: 45px !important;
}
a.textHighlight, a.textHighlight:hover {
    color:#000!important;
    background-color: #fff2a8;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus {
    color:#45e40c;
}
.nbme-bottomOptions, .nbme-topOptions {
    background-color: #364265;
}
#nbme-dynamicTopButtonsDiv .nbme-button-open, #nbme-dynamicTopButtonsDiv button:hover, #nbme-staticTopButtonsDiv button:hover, #nbme-topLeftButtonsDiv label:hover, .nbme-footer-right button:hover {
    background-color:#293559;
    border-style:inset
}
#markQuestion {
    border: 1px solid #364265;
}
#nbme-dynamicTopButtonsDiv button, #nbme-staticTopButtonsDiv button, #nbme-staticTopButtonsDiv label, .nbme-footer-right button {
    border: 1px solid #364265;
}
.daymode .nbme-leftcontent {
    background-color: #1B2337;
    scrollbar-width: thin;
    color: #fffaf0;
    scrollbar-color: #90A4AE #CFD8DC;
}
.daymode .nbme-leftcontent .selected {
    cursor:default;
    background-color:#5d87f0;
}
.daymode .nbme-leftcontent .alternate {
    background-color:#2f467d;
    color: #fffaf0;
}
.nav-tabs>li.active>a:hover {
    color: #9cffff;
}
.table-header-footer-style tr:first-child {
    font-size:1.2em;
    font-weight:600;
    border: 1px solid white;
}
#questionText table tr td, #explanation table tr td, #questionAbstract table tr td, #hintBox table tr td, .exhibit table tr td, #collegeprepHintPopup table tr td {
    border: 1px solid white;
}
.answerStatsBar .headerText {
    color: #9cffff;
    font-size: 14px !important;
}
em {
    color: #9cffff;
}
.progress {
    background-color: #fdfcd2;
}
.white-bg {
    background-color: unset;
}
b, strong {
    font-weight: 700;
    color: #EAB754;
}
.nbme-centercontent {
    background-color: #1B2337;
    color: #FFEAC1;
}
.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #123251;
}
.form-control, .single-line {
    color: #000;
}
.opac08 {
    color: #4dc2f0;
}
.opac04 {
    color: #4dc2f0;
}
.table-hover > tbody > tr:hover {
    background-color: #023d71;
}
.datatableCustomHeader {
    background-color: unset;
}
.nbme .answerChoiceBackground:hover {
    background-color: #133079;
    color:#fffaf0;
}
p {
    margin: 40px 0 10px;
}
.answerStatsBar .subHeaderText {
    color:#fff;
    font-size:14px!important
}
}
