css:
@-moz-document domain("dlthe.com") {
.top_home_link_td {
    width: 146px;
    vertical-align: top;
    background: url(https://i.imgur.com/sfwxbxv.png) no-repeat;
}
.top_home_link_td a:active {
    width: 146px;
    vertical-align: top;
    background: url(https://i.imgur.com/34DX9z4.png) no-repeat;
    margin-top: -4px;
    margin-left: -8px;
}
.top_home_link_td a {
    width: 146px;
    vertical-align: top;
    background: url(https://i.imgur.com/sfwxbxv.png) no-repeat;
    margin-top: -4px;
    margin-left: -8px;
}
#page_header {
    height: 39px;
    width: 791px;
    margin: 0px auto;
    z-index: 120;
    background: url(https://i.imgur.com/MWnXTHB.png);
}
#page_header .right {
    background: rgba(0, 121, 255, 0) url(https://i.imgur.com/ruYZEpD.png) 100% -1px;
}
.head_nav a.top_nav_link {
    display: block;
    padding: 12px 10px 13px;
    height: 13px;
    margin: 0px;
    font-weight: bold;
    font-size: 12px;
    color: #E8EFF7;
    text-shadow: 0px 1px 0px #4E7097;
}
.top_back_link_td {
    background: url(https://i.imgur.com/MWnXTHB.png);
}
#page_header {
    background: url(https://i.imgur.com/1QEaNqq.png);
}
#page_header .content {
    background: url(https://i.imgur.com/1QEaNqq.png) no-repeat;
    height: 39px;
}
#page_body {
    margin-top: 0px;
}
#page_header .left {
    background: rgba(255, 0, 0, 0);
    background: url(https://i.imgur.com/sfwxbxv.png) no-repeat;
}
#page_header .back {
    position: absolute;
    width: 100%;
    height: 40px;
    background: #4e729a00 url(https://i.imgur.com/y1MKKh0.png);
}
.input_back_content {
    padding: 1px 2px;
    white-space: nowrap;
    line-height: normal;
}
