css:
@-moz-document domain("busuu.com") {
/* Background color */
.viewport--quaternary-light {
    background-color: #0f0f0f !important;
}

/* Profile header color */
.profile-header {
    background: #171717 !important;
    box-shadow: 0 .4375rem .5625rem -.4375rem #000 !important;
}

/* Icon fill color */
.icon--quaternary-base {
    fill: #000 !important;
}

/* Premium icon fill color */
.icon--light {
    fill: #171717 !important;
}

/* Profile picture boarder color */
.profile-info__picture {
    border: .125rem solid #0000 !important;
}

/* Panels on profile */
.panel-empty, .panel-main {
    background-color: #171717 !important;
    box-shadow: 0 0 .625rem -.125rem #0000 !important;
}
.fluency__details {
    border-top: .0625rem solid #0f0f0f !important;
}
.profile-daily .profile__active-days {
    border-left: 1px solid #0f0f0f !important;
}

/* Streak border color */
.day-list-item__circle {
    border: 1px solid #f88800 !important;
}

/* Main text color */

body {
    color: #fff !important;
}

/* Course panel colors */
.course-lesson__head {
    box-shadow: 0 .125rem .5625rem -.3125rem #000 !important;
    color: #fff !important;
}
.course-lesson__body {
    border-top: 1px solid #0000 !important;
}

/* Profile navigation colors */
.profile-nav__item {
    color: #919191 !important;
}

/* Footer colors */
.footer-main {
    background-color: #000 !important;
}
.footer-main__socials__links {
    background-color: #373737 !important;
}

/* Header colors */
.nav-top {
    background-color: #000 !important;
}
.nav-top__item {
    background-color: #000 !important;
}
.viewport__header {
    box-shadow: 0 .4375rem .5625rem -.4375rem #000 !important;
}
.cst__flag-wrap {
    border: .25rem solid #000 !important;
    box-shadow: 0 .1875rem .1875rem 0 rgba(0, 0, 0, 0) !important;
}

/* Study notifaction colors */
.smart-review-banner__wrapper {
    background-color: #000 !important;
    box-shadow: 0 0 .625rem -.125rem #d6dee600 !important;
}
.referral-banner__wrapper {
    box-shadow: 0 0 .625rem -.125rem #d6dee600 !important;
    background-color: #000;
}

/* Estimated study time colors */
.unit-time-indicator {
    border: 1px solid #d6dee600 !important;
    background-color: #000 !important;
}

/* Finished progress bar colors */
.progress-bar__inner--primary {
    background-color: #eea211 !important;
}
.course-lesson__head-percent--complete {
    color: #eea211 !important;
}

/* Progress bar colors */
.progress-bar {
    background-color: #0b0b0b !important;
}
.progress-bar__inner--base {
    background-color: #eea211 !important;
}

/* Notification dropdown colors */
.nav-top__dropdown {
    color: #c8c8c8;
    background-color: #000 !important;
    border: 1px solid #000 !important;
}
.notification-list__item--active {
    background-color: #131313 !important;
}
.nav-top__dropdown li {
    position: relative;
    border-top: 1px solid #d6dee600 !important;
}
.nav-top__dropdown-title {
    color: #7b7b7b !important;
}
.notification-list_avatar {
    border: 1px solid #d6dee600 !important;
}

/* Correction and comment colors */
.comment-row_content {
    border: .125rem solid #000 !important;
}
.comment-row__meta {
    border-bottom: 1px solid #0b0b0b !important;
}
.friend-profile-row {
    border-bottom: 1px solid #0b0b0b !important;
}
.media-player .plyr__controls {
    color: #686868 !important;
    background-color: #0b0b0b !important;
}
.plyr--audio .plyr__control:hover, .plyr--audio .plyr__control[aria-expanded="true"] {
    color: #a8a8a8 !important;
}
.media-player .plyr--full-ui.plyr--audio input[type="range"], .media-player .plyr--full-ui.plyr--video input[type="range"] {
    color: #e1e1e1 !important;
}
.comment-row__award {
    border-bottom: .125rem solid #0b0b0b !important;
}
.comment-row_content_footer {
    background-color: #111 !important;
}
.comment-row_content_extra {
    background-color: #0b0b0b !important;
}
.comment-vote_btn {
    background-color: #060606 !important;
    border: 1px solid #116eee00 !important;
    color: #b5b5b5 !important;
}
.comment-vote_btn--disabled, .comment-vote_btn--disabled:hover {
    background-color: #393939 !important;
    border-color: #0000 !important;
}
.comment-compose_box_editor {
    background-color: #0f0f0f !important;
    color: #fff !important;
}
.comment-compose_preview {
    background-color: #0b0b0b !important;
}
.comment-compose_box {
    border: 2px solid #d6dee600 !important;
}
hr {
    border-top: 1px solid #0000 !important;
}
.comment-compose_comment-editor {
    border: 2px solid #d6dee600 !important;
    background-color: #0b0b0b !important;
    color: #fff !important;
}
.comment-inline__editor {
    color: #fff !important;
    background-color: #000 !important;
    
}
.comment-inline {
    border: .125rem solid #d6dee600 !important;
}
.comment-inline__editor-buttons {
    background-color: #0d0d0d !important;
}
.recording-button {
    background-color: #1c1c1c !important;
    box-shadow: 0 .125rem .625rem 0 #000 !important;
}
.recording-actions__remove {
    box-shadow: 0 .125rem .625rem 0 #000 !important;
    border: .125rem solid #0000 !important;
    background-color: #060606 !important;
}
.correct-list__item {
    border-bottom: 1px solid #0d0d0d !important;
}
.correct-list__header {
    border-bottom: 1px solid #0d0d0d !important;
}
.correct-list-row__link {
    color: #cecece !important;
}
.ex-feedback-bar__note-bar {
    background-color: #000 !important;
}
.ex-dialogue-sentence__content {
    background: #000 !important;
}
.ex-dialogue-sentence--left .ex-dialogue-sentence__content::before {
    border-right-color: #000 !important;
}
.ex-dialogue-sentence--right .ex-dialogue-sentence__content::before {
    border-left-color: #000 !important;
}

/* Social tab colors */
.social__filters {
    background-color: #0b0b0b !important;
}
.social__header {
    border-bottom: 1px solid #0000 !important;
}
.nav__link {
    color: #888 !important;
    border-bottom: .1875rem solid transparent !important;
}
.social-correction {
    border: 1px solid #0d0d0d !important;
}
.social-correction__copy {
    color: #828282 !important;
}

/* Setting colors */
.icon-select__label {
    background-color: #111 !important;
}
.icon-select__dropdown {
    border: 1px solid #111 !important;
}
.form__input, .form__select, .form__textarea {
    border: 1px solid #000 !important;
    color: #fff !important;
    background-color: #1e1e1e !important;
}

/* Course drawer */
.course-sheet__drawer {
    background-color: #000 !important;
}
.course-selector-option {
    background-color: #0d0d0d !important;
}
.course-selector-option__image-wrap {
    border-left: 1px solid #f2f7fd00 !important;
}

/* Lesson colors */
.nav-lesson__fixed-wrapper {
    background: -webkit-gradient(linear,left bottom,left top,color-stop(0,rgba(242,247,253,0)),color-stop(42%,#f2f7fd)) !important;
    background: linear-gradient(0deg,rgba(242,247,253,0),#93939326 42%) !important;
}
.ex__instruction {
    color: #fff !important;
}
.ex-btn--default {
    color: #9b9b9b !important;
    border: .125rem solid #fff0 !important;
    background-color: #060606 !important;
}
.circle-icon__icon {
    background: #0f0f0f !important;
}
.ex-typing__input {
    border-bottom: .125rem solid #171717 !important;
    background-color: #202020 !important;
    color: #fff !important;
}
.ex-phrase-builder__dropzone {
    border: 1px solid #d6dee600 !important;
    background-color: #1a1a1a !important;
}
.ex-btn--used {
    border: .125rem solid #000 !important;
    background-color: hsla(0, 0%, 100%, 0) !important;
}
.ex-tip__table td:not(:last-child) {
    border-right: 1px solid #000 !important;
}
.ex-tip__table tr:not(:last-child) {
    border-bottom: 1px solid #000 !important;
}
.ex-tip__table {
    border-collapse: collapse;
    background-color: #171717 !important;
    width: 100%;
}
.ex-tip__examples {
    border: 1px solid #000 !important;
}
.ex-btn__dash rect {
    fill: #0000 !important;
    stroke: #000 !important;
}
.ex-btn__jagged--down::after {
    background-image: linear-gradient(155deg,#000 50%,transparent 0),linear-gradient(-155deg,#000 50%,transparent 0) !important;
}
.ex-btn__jagged--up::after {
    background-image: linear-gradient(25deg,#000 50%,transparent 0),linear-gradient(-25deg,#000 50%,transparent 0) !important;
}
.ex-btn--text {
    color: #606060 !important;
}
.progress-banner-bar__content.drawer__content {
    background-color: #000 !important;
}
.screen-result__activity-icon {
    background-color: #060606 !important;
}
.icon--brand-base {
    fill: #eea211;
}

/* Referral junk */
.referral__wrap {
    background-color: #000 !important;
    box-shadow: 0 0 .625rem -.125rem #d6dee600 !important;
}
.referral-friends-list__avatar--count, .referral-friends-list__avatar--empty {
    background: #131313;
}
.icon--quaternary-light {
    fill: #626262;
}

/* Review stuff */
.smart-review__header {
    background-color: #000 !important;
}
.smart-review__wrap {
    box-shadow: 0 0 .625rem -.125rem #d6dee600 !important;
}
.vocab-summary__progress-graphs {
    background: linear-gradient(180deg,#0d0d0d,#0d0d0d) !important;
}
.vocab-strength-graph__bucket-container {
    background-color: #000 !important;
    box-shadow: inset 0 0 2px #0000 !important;
}
.vocab-strength-graph__count {
    color: #b5b5b5 !important;
}
.vocab-strength-graph__title {
    color: #404040 !important;
}
.vocab-list-row:nth-child(2) {
    border-top: 1px solid #0f0f0f !important;
}
.vocab-list-row:hover {
    background-color: #1c1c1c !important;
}
.vocab-list-row {
    border-bottom: 1px solid #0f0f0f !important;
}
}
