css:
@-moz-document domain("developer.playcanvas.com") {

.hljs, .hljs-tag, .css .hljs-rules, .css .hljs-value, .css .hljs-function .hljs-preprocessor, .hljs-pragma {
    color: #f8f8f2;
    font-family: Fira Code, monospace;
} /**/

body {
    font-family: "Proxima Nova Regular","Helvetica Neue",Arial,Helvetica,sans-serif;
    font-weight: normal;
    font-style: normal;
    padding: 0;
    margin: 0;
    color: #324447;
    background-color: #222;
} /**/

body>.container>main .methodname {
    font-size: 16px;
    font-family: Fira Code, monospace;
    color: #fff;
} /**/

body>.container>main p {
    color: #999;
} /**/

body>.container>main
{
    color: #eee;
} /**/

body>.container>main a {
    color: #0af;
    font-size: 12px;
    font-family: Fira Code, monospace;
}
body>.container>nav.sidebar>ul>li>a
{
    color: #999;
    display: block;
    line-height: 32px;
    font-size: 12px;
    font-family: Fira Code, monospace;
} /**/

body>.container>nav.sidebar>ul>li:hover>a {
    color: #fff;
    font-size: 12px;
    font-family: Fira Code, monospace;
    font-weight: bold;
} /**/

body>.container>nav.sidebar>ul>li:hover:before, body>.container>nav.sidebar>ul>li.active:before {
    color: #fff;
    content: "";
    position: absolute;
    left: -20px;
    width: 16px;
    line-height: 32px;
    text-align: center;
    font-family: "ui-font-solid";
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-smoothing: antialiased; /**/
} /**/

body>.container>main h1, body>.container>main h2, body>.container>main h3, body>.container>main h4, body>.container>main h5 {
    font-family: "Proxima Nova Bold","Helvetica Neue",Arial,Helvetica,sans-serif;
    font-weight: bold;
    font-style: normal;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    margin-bottom: 0px;
    color: #aaa;
} /**/

.container nav.usermanual ul li > a {
    color: #999;
}/**/
.container nav.usermanual ul li:hover > a {
    color: #fff;
    font-weight: bold;
}/**/
}
