css:
@-moz-document url(http://www.example.com/test.html) {
      body {
          color: #fff;
          background-color: #000;
      }

      #page {
          color: #fff;
          background-color: #000;
      }

      .byline a {
          color: #fff;
      }

      .widgettitle a, .stories h3.widgettitle a {
          color: #fff;
      }

      .navbar .navbar-inner {
          background-color: #3c3c3c;
      }

      .navbar .nav > li > a {
          color: #fff;
      }

      body .sticky-nav-holder.main_nav_hide_article, body .sticky-nav-holder.sticky_nav_display {
          background-color: #3c3c3c;
      }

      .navbar.sticky-navbar li.dropdown .dropdown-menu li a {
          color: #fff;
      }

      #header-social i {
          color: #fff;
      }

      .navbar.sticky-navbar li a {
          line-height: 56px;
          color: #fff;
      }

      .navbar li.dropdown .dropdown-menu li a {
          color: #fff;
          background-color: #3c3c3c;
      }

      .stories .entry-content p {
          background-color: #3c3c3c;
      }

      .archive .entry-content.with-hero, .home .entry-content.with-hero {
          background-color: #3c3c3c;
      }

      textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
          background-color: #6f6f6f;
          border: 1px solid #ccc;
          color: #fff
      }

      select, textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
          background-color: #6f6f6f;
          color: #fff;
      }

      .author-box {
          background-color: #6f6f6f;
      }
}
