css:
@-moz-document domain("forums.torque3d.org") {
body {
    background-color: #222222;
    color: #eeeeee;
}

a {
    color: #5889f2;
}
.site-name a, .folder a, .breadcrumb li a, h3 a {
    color:#ffffff;
}

.top-bar {
    margin-bottom: 30px;
    background: rgb(83, 82, 82) url(https://forums.torque3d.org/styles/torque3d/theme/images/site_logo.png) no-repeat 30px 10px;
    background-size: 150px;
    border-bottom: none;
}
.btn-default {
    color: #f2f2f2;
    background-color: #353535;
    border-color: #666464;
}
.form-control {
    color: #f7f5f5 !important;
    background-color: #515151 !important;
    border: 1px solid #666464;
    border-radius: 2px;
}

.input-group-addon {
    color: #fff;
    background-color: #515151 !important;
    border: 1px solid #666464;
    border-radius: 2px;
}
#nav-breadcrumbs {
    border: 0px solid #575757;
    background-color: #353535;
    border-radius: 0;
}
.navbar-default {
    background-color: #262626 !important;
    border-color: #313131;
}
.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:focus, .navbar-default .navbar-nav>.open>a:hover {
    color: #e8e8e8;
    background-color: #3c3c3c;
}

.dropdown-menu {
    background-color: #444444;
    border: none;
    border-radius: 0;
}
.dropdown-menu li a {
    color: #ffffff;
}
.panel {
    background-color: #313131;
    border: 1px solid transparent;
    border-radius: 2px;
}
.panel-default {
    border: none;
}
div.icon {
    border-top: 0px solid #dddddd;
}
.bg1 {
    background-color: #333333;
}
.bg2 {
    background-color: #2b2b2b;
}
.btn-default.disabled, .btn-default.disabled.active, .btn-default.disabled.focus, .btn-default.disabled:active, .btn-default.disabled:focus, .btn-default.disabled:hover, .btn-default[disabled], .btn-default[disabled].active, .btn-default[disabled].focus, .btn-default[disabled]:active, .btn-default[disabled]:focus, .btn-default[disabled]:hover, fieldset[disabled] .btn-default, fieldset[disabled] .btn-default.active, fieldset[disabled] .btn-default.focus, fieldset[disabled] .btn-default:active, fieldset[disabled] .btn-default:focus, fieldset[disabled] .btn-default:hover {
    background-color: #202020;
    border-color: #aaa8a8;
}
select, input.inputbox {
    color: #f7f5f5;
    background-color: #515151;
    border: 1px solid #666464;
    border-radius: 2px;
}
.well {
    background-color: #242424;
    border: 0px solid #e3e3e3;
    border-radius: 2px;
}
.panel-footer {
    background-color: #222222;
    border-top: 0px solid #ddd;
}
}
