/**********************************/



@-moz-document domain("hattrick.org") {



#top

{

height: 0px !important;

position: relative !important;

z-index: 50 !important;

background: #000000 !important;

}

#footer a

{

color: #000000 !important;

font-weight: bold !important;

}

#ticker

{

position: absolute !important;

width: 350px !important;

top: 20px !important;

left: 360px !important;

}

#ticker div a

{

padding-bottom: 2px !important;

color: #cc0000 !important;

text-decoration: none !important;

line-height: 1.1em !important;

}

#shortcuts

{

margin-top: 0px !important;

margin-left: 0px !important;

}

#teamLinks

{

color: #cc0000 !important;

margin-top: 15px !important;

margin-left: 7px !important;

}

div#teamLinks a

{

color: #ffffff!important;

}

/*--Users online - week - date - time ----------------------------*/

div[id="online"] {color: black !important;}

div[id="time"] {color: black !important;}



/*--Page background-----------------------------------------------*/

body {background: black !important;}



/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/

h1

{

color: #cc0000 !important;

border-bottom: solid thin #ff0000 !important;

}

/*-----all boxHead--------------------*/

h2

{

color: #cc0000 !important;

background: black !important;

}

/*-----centre news--------------------*/

.boxBody h2

{

color: #cc0000 !important;

background: none !important;

border-bottom: solid 1px #ff0000!important;

}

/*-----------boxHeads----------------------------------------------------------------------------------------*/

/*Left menu*/

.subMenuBox .boxHead

{

color: #cc0000!important;

background: black !important;

}

.subMenuBox .boxHead .boxLeft

{

color: #cc0000 !important;

background: black !important;

}

/*Centre box*/

.main .boxHead h2

{

color: #cc0000 !important;

background: black !important;

}

/*Right menu*/

.sidebarBox h2

{

color: #cc0000 !important;

background: black !important;

}

/*----- Press, GB, Team events ------------------------------------------------------------------------------*/

.main .mainBox h2

{

border-bottom: solid 1px #996600 !important;

color: #000000 !important;

font-size: 14px !important;

font-weight: bold !important;

padding: 1px !important;

}

/*Central header paths*/

h2 a

{

color: #996600 !important;

}

h2 a:hover

{

color: #ffffff !important;

}

/*News and team events date*/

.date

{

color: #cc0000 !important;

padding-right: 8px;

font-weight: 400;

}

/*Left Menu titles*/

h3

{

color: #996600 !important;

border-bottom: solid thin #996600 !important;

}

/*General Links*/

a

{

color: #996600 !important;

text-decoration: none !important;

}

/*----subMenu and sidebar links------------------------------------------------------------------------------*/

.subMenuBox a, .subMenuBox a

{

text-decoration: none !important;

color: #000000 !important;

}

.sidebarBox a

{

text-decoration: underline !important;

color: #cc0000 !important;

}

/*Bot teams link*/

div a[class="shy"]

{

color: gray !important;

}

/*Hide Hattrick logo*/

#logo

{

background: none !important;

}

/*Hide ticker's stars*/

#ticker div

{

/*background: none !important;*/

}

/* central page background */

#content

{

background-image: none !important;

background-color: #cc0000 !important;

}

/*Image header (banner)*/

#header

{

background: url(http://img337.imageshack.us/img337/9812/sinttulo1m.png) !important;

height: 102px !important;

padding-top: 40px !important;

}

/*--------Top menu settings----------------------------------------------------------------------------------*/

#menu

{

height: 20px !important;

margin-top: 5px !important;

background: black !important;

}

#menu a

{

line-height: 20px !important;

color: #996600!important;

}

div#menu a:hover

{

color: black !important;

background: #996600 !important;

}

a:hover

{

color: #996600 !important;

}

/*bottom white line*/

div[id="bottom"]

{

background: #cc0000 !important;

}



/*---Aufstellung---------------------------------------------------------*/

div[class="name"] a {color: #000000 !important;}

div[class="name"] a:hover {color: #cc0000 !important;}



/*---Forum---------------------------------------------------------------*/

div[class="boxBody"] {background: white !important;}

div[class="message"] a {color: #996600 !important;}

div[class="signature"] a {color: #996600 !important;}



#cfPollHeader

{

background: black !important;

padding: 5px 10px;

font-weight: bold;

color: #ffffff;

}

.cfWrapper

{

border: solid 1px #cc0000 !important;

margin: 0 0 12px 0;

}

.cfWrapper a

{

color: #996600 !important;

}

.cfUserInfo a

{

color: #cc0000 !important;

}

.folderHeader a:link

{

color: #cc0000 !important;

font-weight: bold !important;

}

.cfFooter a

{

color: black !important;

}

.folderItem div.ignore

{

cursor: pointer;

width: 10px;

height: 10px;

}

.folderItem div.unignore

{

cursor: pointer;

width: 10px;

height: 10px;

}

.folderItem div.unignore:hover

{



}

/*Ads section*/

div[class="insertHeader"]

{



}

div[class="insertFooter"]

{

background: #ffffff !important;

}

/*-------------Formatting buttons----------------------------------------------------------------------------*/





/*shortcuts*/

img.scNewMail

{



}

}
