css:
@-moz-document domain("forum.minesucht.net") {
body {
	    background-color: rgb(23, 23, 23);
	    color: rgb(238, 238, 238) !important;
	}
	/* ------------- */
	/* |Allgemeines| */
	/* ------------- */
	.benutzerstyle {
	    color: white
	}
	input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="url"], select, textarea, input[type="date"]:hover, input[type="datetime"]:hover, input[type="email"]:hover, input[type="number"]:hover, input[type="password"]:hover, input[type="search"]:hover, input[type="tel"]:hover, input[type="text"]:hover, input[type="url"]:hover, select:hover, textarea:hover {
	    background-color: black;
	}
	/* ----------
	/* |Beiträge| */
	/* ---------- */
	.messageSidebar {
	    background-color: black;
	}
	.inputItemList {
	    border: none;
	    background-color: rgb(23, 23, 23)
	}

	/* Profil Rechts */
	.boxesSidebarLeft .box:not(.boxBorderless), .boxesSidebarRight .box:not(.boxBorderless) {
	    background-color: rgb(0, 0, 0);
	}
	/* -------- */
	/* |Header| */
	/* -------- */
	.pageHeaderPanel {
	    background-color: black;
	}
	.pageNavigation {
	    background-color: rgb(23, 23, 23);
	    color: rgb(238, 238, 238);
	}
	/* Header (Bild) */
	.pageHeaderContainer {
	    background: none !important;
	}
	.pageHeaderFacade {
	    display: none !important
	}
	/* ------------------ */
	/* |Forum Startseite| */
	/* ------------------ */
	.dialogContainer > .dialogContent {
	    background-color: rgb(23, 23, 23) !important
	}
	/* ------------------ */
	/* |Forum Kategorien| */
	/* ------------------ */
	.wbbBoardList {
	    background-color: rgb(23, 23, 23);
	    color: rgb(238, 238, 238);
	}
	.content > .section, .content > form, .sectionContainer {
	    border: none !important;
	    background-color: rgb(23, 23, 23);
	}
	/* ------------------- */
	/* |Header Navigation| */
	/* ------------------- */
	.userPanel > ul {
	    background-color: black;
	}
	.userPanel > ul > li > a {
	    background: none;
	}
	.interactiveDropdownHeader {
	    background-color: black;
	}
	.interactiveDropdown {
	    background-color:black;
	}
	.interactiveDropdownShowAll {
	    background-color: black;
	}
	/* -------- */
	/* |Footer| */
	/* -------- */
	.boxesFooterBoxes {
	    background-color: black;
	}
	.boxesFooter {
	    background-color: black;
	}
	.pageFooterCopyright {
	    background-color: black;
	}
	.boxesFooterBoxes .boxContainer {
        display: none
    }
	/* Footer Text */
	.pageFooterCopyright > div > div > a {
	    color: transparent !important;
	    font-size: 0 !important;
	}
	.pageFooterCopyright > div > div > a::after {
	    content:"Community-Software: WoltLab Suite™ - Style von Zurani" !important;
	    font-size: 14px !important;
	    color: white !important;
	    font-weight: normal !important;
	    font-family: Segoe UI, Tahoma, sans serif !important;
	}
	/* Stil ändern */
	.wbbBoardList .tabularBoxTitle > header {
	    background-color: rgb(23, 23, 23);
	}
	/* -------- */
	/* |Profil| */
	/* -------- */
	/* Profil Kommentar */
	.commentListAddComment.collapsed {
	    background-color: black;
	}
	.redactor-box, .messageTabMenuNavigation > ul, .messageTabMenu > .messageTabMenuContent.active, .messageContent.loading > .messageContentLoadingOverlay {
	    background-color: rgb(23, 23, 23);
	}
	.redactor-toolbar {
	    background-color: black;
	}
	.boxesSidebarLeft .box .boxMenu li.active > .boxMenuLink, .boxesSidebarRight .box .boxMenu li.active > .boxMenuLink {
	    background-color: rgb(23, 23, 23);
	}
}
