css:
@-moz-document regexp('(https?:\/\/)?(w{3}.)?pub.dev') {
:root {
    --clear: #e4e4e4;
    --glossDark: rgba(0, 0, 0, 0.2);
    --glossDarker: rgba(0, 0, 0, 0.3);
    --bodyBg: #153535;
}
body,
.home-block.home-block-ff,
.home-block {
    background: var(--bodyBg);
    color: var(--clear);
}

body.non-experimental .list-item > .description,
.search-filters-btn.search-filters-btn-wrapper.search-controls-more {
    color: var(--clear);
}

a {
    color: #128bdb;
}

header#title,
ul.detail-tabs-header,
.packages .packages-item:hover {
    background: var(--glossDarker);
}

header > ol > li > a,
.search-controls .search-controls-tabs .list-filters .filter {
    color: aliceblue;
}

body.non-experimental .detail-tabs-header > .tab-button.-active,
body,
.markdown-body {
    color: #dbdbdb;
    border-bottom-color: #dbdbdb;
    font-weight: 800;
}

.markdown-body code,
code,
.home-block .mini-list-item {
    background-color: var(--glossDark);
}

code,
li.list-item.-full:hover,
li.list-item.-simple:hover {
    background: var(--glossDark);
    border: none;
}
.markdown-body .highlight pre,
.markdown-body pre,
.hljs {
    background: var(--glossDark);
    border-radius: 15px;
}
.hljs-keyword {
    color: #85ddc9;
}

pre code.language-dart.hljs {
    color: #e5fff9!important;
}

.hljs-string,
.hljs-doctag {
    color: #ff2b5f;
}

h1,
h2,
h3,
h4,
h5,
h6,
.home-block .home-block-title {
    color: #f2f2fd;
}

code.language-yml.hljs.yaml,
.hljs,
.hljs-subst,
body.non-experimental .detail-header .metadata,
.non-experimental .package-tag,
.signature,
dd,
.detail-tabs-header > .tab-button,
.detail-tabs-header > .tab-link > a {
    color: #ededff;
}

.features {
    color: #ededffa4;
}

body.non-experimental .detail-info-box {
    background: var(--glossDark);
    border: none;
    border-radius: 15px;
}

body.non-experimental .detail-tabs-header > .tab-button,
body.non-experimental .detail-tabs-header > .tab-link > a {
    color: #dbdbdb;
}


a.package-tag,
.non-experimental .package-tag s,
.markdown-body table tr ,
.search-controls .search-controls-advanced{
    background: #204a4a;
    color: var(--clear);
    border-bottom-color: #204a4a;
}

.markdown-body table tr:nth-child(2n),
.search-controls {
    background: #275959;
}

.pagination > li {
    background: var(--glossDark);
}
.pagination > li:hover,
.home-block .mini-list-item:hover {
    background: var(--glossDarker);
}

body.non-experimental .listing-page-title {
    color: aliceblue;
}

body.non-experimental .sub-nav{
    background: #585859;
    border: none;
    color: var(--clear);
}

.markdown-body img,
.-wide-header-detail-page .detail-header,
.-wide-header-detail-page .detail-tabs-wide-header {
    background: none;
}

div.detail-container > div.detail-header-outer-block > div.detail-header-content-block > h1.title {
    font-size: 28px;
}

pre {
    background: none;
}

.home-block .mini-list-item:hover .mini-list-item-body:before,
.home-block .mini-list-item .mini-list-item-body:before {
    background: none;
}

div.pkg-page-title-copy-feedback {
    color: var(--bodyBg);
}

.sort-control .sort-control-popup .sort-control-option.selected{
    background-color: #204949;
}

.sort-control .sort-control-popup .sort-control-option:hover{
    background-color: #203939;
}

.sort-control .sort-control-popup .sort-control-option{
    background-color: #275959;
}


/*Images*/
#-pub-like-icon-button,
i.email-icon,
i.search-icon,
img[alt^="Go to the documentation of"],
img[alt^="Download "] {
    filter: invert(83%);
}

.landing-main {
    background-image: none;
}

}
