css:
@-moz-document url-prefix("https://balkan-samp.com/forum/index.php"), url-prefix("https://balkan-samp.com") {
body {
    background: linear-gradient(180deg, rgb(43, 43, 43) 0%, rgb(117, 61, 0) 100%);
    font: 12px/130% Tahoma, sans-serif;
    margin: 0;
    padding: 0;
    background-size: cover;
}
#toolbar {
    height: 36px;
    background: linear-gradient(90deg, rgb(43, 43, 43) 0%, rgb(242, 125, 0) 100%);
    border-top: solid 1px #606060;
}
#header-tab {
    background: #222;
    height: 25px;
    border-bottom: solid 1px #606060;
}
h3.catbg {
    background: linear-gradient(90deg, rgb(43, 43, 43) 0%, rgb(242, 125, 0) 100%);
    padding-right: 9px;
}
div.cat_bar {
    background: #2b2b2b;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
    border-radius: 10px 20px 0px 10px;
}
div.sp_block {
    overflow: hidden;
    padding: 0 2em;
}
#header {
    position: relative;
    height: 120px;
    overflow: hidden;
    background: url("https://i.imgur.com/ps7AbGj.png") repeat-x;
}
.avatar {
    border-radius: 180px;
    border: 3px solid #f27d00;
}
#footer {
    padding: 20px 40px 20px 40px;
    font-size: 14px;
    color: #000;
    border-top: solid 1px #606060;
    text-align: center;
    background: #40444e;
    border-radius: 0px 0px 30px 30px;
}
div.title_barIC {
    background: #40444e;
    padding-left: 9px;
    height: 31px;
    overflow: hidden;
    margin-bottom: 1px;
    border-radius: 0px 0px 10px 10px;
}
div.title_barIC h4.titlebg {
    background: #40444e;
}
.poster h4, .poster ul li {
    background: none !important;
    border: none;
    margin-bottom: 2px;
    padding-top: 4px;
    padding-bottom: 4px;
    width: 95%;
    overflow: hidden;
}
li.postgroup {
    background: #40444e;
    border-radius: 0px 0px 10px 10px;
}
.poster {
    float: left;
    padding-left: 8px;
    width: 220px;
    box-shadow: 1px 1px 0.1px 0.2px #ff8300;
    background: url(https://i.ibb.co/R3FbZ9d/momenziback.gif);
    background-size: auto;
    border-radius: 25px 0px 35px;
}
.description, .description_board, .plainbox {
    padding: 0.5em 1em;
    font-size: 0.9em;
    line-height: 1.4em;
    border: 1px solid #bbb;
    background: #40444e;
    margin: 0.2em 1px 1em 1px;
    color: #fff;
}
#userinfo {
    margin: 10px;
    font-size: 11px;
    color: #fff;
    float: right;
    padding: 12px 12px;
    text-align: right;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid #222;
    font-weight: bold;
    text-shadow: 2px 2px 2px black;
    border-radius: 0 0 0 25px;
}
#userinfo a:link, #userinfo a:visited {
    color: #ffa722;
    font-weight: bold;
}
a img {
    border: 0;
    border-radius: 10px;
}
.poster h4, .poster h4 a {
    font-style: italic;
}
.postarea, .moderatorbar {
    margin: 0 0 0 250px;
}
#post_header dt, .postbox dt {
    float: left;
    padding: 5px;
    width: 15%;
    margin: .5em 0 0 0;
    font-weight: bold;
}
input, button, select, textarea {
    font: 95%/115% verdana, Helvetica, sans-serif;
    color: #000;
    background: #fff;
    border: 1px solid #7f9db9;
    padding: 6px;
    border-radius: 10px;
}
.button_submit, .button_reset {
    cursor: pointer;
    background: #f27d00;
    color: #FFF;
    padding: 10px 20px;
    border-radius: 3px;
    font-size: 12px;
    line-height: 12px;
    font-weight: bold;
    border: none;
}
.button_submit:hover, .button_reset:hover {
    color: #FFF;
    background: #e77700;
}
input:hover, textarea:hover, button:hover, select:hover {
    border: none !important;
}
#mainarea {
    border-top: solid 1px #2b2b2b;
    background: #2b2b2b;
    padding: 0 2px 10px;
}
.windowbg, #preview_body {
    background-color: #2b2b2b;
}
body, td, th, tr {
    color: #ffffff;
}
a:link, a:visited {
    color: #fff;
    text-decoration: none;
}
strong {
    font-weight: bold;
    color: #fff;
}
.windowbg span.topslice span {
    display: none;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/main_block.png") 100% -30px no-repeat;
    height: 15px;
}
.windowbg span.botslice span {
    display: none;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/main_block.png") 100% -40px no-repeat;
    height: 11px;
}
#footer {
    padding: 20px 40px 20px 40px;
    font-size: 14px;
    color: #fc9a04;
    border-top: solid 1px #606060;
    text-align: center;
    background: #2b2b2b;
    border-radius: 0px 0px 30px 30px;
}
#footer a:link, #footer a:visited {
    color: #fff;
    text-decoration:none;
}
#header {
    position: relative;
    height: 120px;
    overflow: hidden;
    background: url(https://i.ibb.co/xYLKRz7/Untitled-1.png) repeat-x;
}
.navigate_section ul li a {
    color: #f27d00;
    font-weight: bold;
    white-space: pre;
}
#userinfo {
    margin: 10px;
    font-size: 11px;
    color: #fff;
    float: right;
    padding: 12px 12px;
    text-align: right;
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid #222;
    font-weight: bold;
    text-shadow: 2px 2px 2px black;
    border-radius: 0 25px 0 25px;
}
.table_list tbody.content td.children {
    background: #40444e;
    color: #555;
    font-size: 85%;
}
.windowbg2 {
    background-color: #2b2b2b;
}
.windowbg2 span.topslice span {
    display: none;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/main_block.png") 100% -60px no-repeat;
    height: 11px;
}
table.table_list tbody.content td.icon {
    text-align: center;
    width: 6%;
    background: #40444e;
    border-radius: 5px 25px 5px 25px;
}
table.table_list tbody.content td.stats {
    font-size: 90%;
    width: 15%;
    text-align: center;
    background: #40444e;
    border-radius: 0px 5px 0px 5px;
}
.roundframe {
    padding: 0 10px;
    background: #2b2b2b;
    border-left: 1px solid #565656;
    border-right: 1px solid #565656;
}
span.upperframe span {
    padding: 0;
    height: 12px;
    display: none;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/main_block.png") 100% -90px no-repeat;
}
.thank_you_button {
    background: url(https://i.imgur.com/gEZuMfr.png) no-repeat transparent;
}
element.style {
    color: white;
}
.windowbg2 span.botslice span {
    display: none;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/main_block.png") 100% -71px no-repeat;
    height: 11px;
}
h1, h2, h3, h4, h5, h6 {
    color: #fff;
    font-size: 1em;
    margin: 0;
    padding: 0;
}
blockquote.bbc_standard_quote {
    background-color: #2b2b2b;
}
input, button, select, textarea {
    font: 95%/115% verdana, Helvetica, sans-serif;
    color: #fff;
    background: #2b2b2b;
    border: 1px solid #ffffff;
    padding: 6px;
    border-radius: 10px;
}
.lockedbg {
    background: #131313;
}
.lockedbg2 {
    background: #131313;
}
table.table_grid td {
    padding: 3px;
    border-bottom: 1px solid #4e4e4e;
    border-right: 1px solid #4c4c4c;
}
.stickybg2 {
    background: #7b7875;
}
.stickybg {
    background: #7b7875;
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    font-size: 11px;
    color: #ffffff;
    line-height: 1.4em;
    background: url("https://balkan-samp.com/forum/Themes/bsold/images/theme/quote.png") 0.1em 0.1em no-repeat;
    border: 1px solid #cfcfcf;
    padding: 1.1em 1.4em;
    margin: 0.1em 0 0.3em 0;
    overflow: auto;
}
a img {
    border: 0;
    border-radius: 25px 0px 25px 10px;
}
#topnav li ul {
    background: #40444e;
    border: 1px solid #2b2b2b;
    position: absolute;
    min-width: 140px;
    left: -999em;
    padding: 4px 0;
    margin-left: -1px;
    margin-top: 7px;
}
.dropmenu li a.active {
    background: url(https://i.ibb.co/CnBygJr/menu-gfx.png) no-repeat 100% 0;
    color: #fff;
    font-weight: bold;
}
.dropmenu li ul {
    z-index: 90;
    display: none;
    position: absolute;
    width: 19.2em;
    font-weight: normal;
    border-bottom: 1px solid #999;
    background: url(https://i.ibb.co/CnBygJr/menu-gfx.png) 0 -100px no-repeat;
    padding: 7px 0 0 0;
}
.calendar_today {
    background-color: #ff9900;
}
.statsbar div.bar div {
    position: relative;
    right: -4px;
    padding: 0 4px 0 0;
    background: url(https://i.ibb.co/CKPvYH5/bar-stats.png) no-repeat 100%;
    height: 16px;
}
.statsbar div.bar {
    float: left;
    background: url(https://i.ibb.co/CKPvYH5/bar-stats.png) no-repeat;
    display: block;
    margin: 0 4px;
    height: 16px;
}
ul.quickbuttons li.quote_button {
    background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 0;
}
ul.quickbuttons li.quote_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 0;
}
ul.quickbuttons li.remove_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -30px;
}
ul.quickbuttons li.modify_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -60px;
}
ul.quickbuttons li.approve_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -90px;
}
ul.quickbuttons li.restore_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -120px;
}
ul.quickbuttons li.split_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -150px;
}
ul.quickbuttons li.reply_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -180px;
}
ul.quickbuttons li.reply_all_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -180px;
}
ul.quickbuttons li.notify_button
{
	background: url(https://i.ibb.co/FXWRSsP/quickbuttons2.png) no-repeat 0 -210px;
}
}
