css:
@-moz-document domain("www.nitrotype.com") {
path[fill="#D62F3A"] {
	fill: blue;
}
body {
	background-color: #2d2e2e;
	overflow-y: scroll;
	background: none;
	overflow-x: hidden;
	position: relative;
}
.card--grit {
	background-image: url("https://wallpaperaccess.com/full/1402732.jpg");
	background: fixed;
}
html {
	background: fixed url("http://www.allwhitebackground.com/images/7/Sapphire-Background-High-Quality-Image.jpg") no-repeat, #2d2e2e;
	/*box-sizing: border-box;*/
	color: #2d2e2e;
	font-family:"Montserrat", sans-serif;
	font-size: 16px;
	font-weight: 300;
	line-height: 1.6;
	background-size: 120%;
}
.bg--gradient {
	background: fixed;
}
.card--a {
	background-image: url("https://wallpaperaccess.com/full/1402732.jpg");
	color: #fefeff;
	background-size: 130%;
}
.btn--primary {
	background: #3705ff;
	color: #ffffff;
}
.btn--primary:hover {
	background: #7602f2;
}
.theme--pDefault .profile-title {
	color: #FFFFFF;
	text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.theme--pDefault .profile-levelNum {
	color: #FFFFFF;
	text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
}
.tc-t {
	color: #ffffff;
}
.theme--pDefault .profile-body {
	color: #FFFFFF;
}
.dash-copyContainer {
	background: #1d1e23;
	-webkit-box-shadow: 0px 1px 157px 10px rgba(0, 0, 0, 0.78);
	box-shadow: 0px 1px 157px 10px rgba(0, 0, 0, 0.78);
	border-radius: 5px;
	flex: 1;
	overflow: hidden;
	padding: 15px;
	display: flex;
}
.dash-copy {
	color: #00abff;
	font-family:"Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
	font-size: 28px;
}
::selection {
	background-color: #1976c6;
	color: white;
}
.bg--gradient {
	background: fixed;
}
.theme--pDefault .profile-bg {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
	color: #d3d3d3;
}
.btn--inky, .btn--inky:focus:not(:hover) {
	color: #ffffff;
}
.theme--pDefault .profile-bgHead {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}
.theme--pDefault.is-gold .profile-bg {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
}
.theme--pDefault.is-guest .profile-bgHead {
	background: #fff0;
}
tr.table-row {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") !important;
}
.gridTable-cell {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") !important;
}
.gridTable--raceResults .gridTable-row:first-child:not(.is-wampus):after {
	background: #003efa;
	box-shadow: 0px 10px 12px 0px rgba(174, 146, 36, 0.3);
}
.gridTable--raceResults .gridTable-row:first-child:not(.is-wampus).is-self:before {
	background: rgba(250, 217, 93, 0.2);
}
.is-active {
	background: rgba(59, 83, 255, 0.25) !important;
}
.card-cap {
	background: url("https://wallpaperaccess.com/full/1402732.jpg") !important;
}
.card--team .card-cap {
    background: url("https://wallpaperaccess.com/full/1402732.jpg") !important;
}
}
