css:
@-moz-document regexp("https://www.twitch.tv/.*") {
.chat-list{
    position: absolute !important;
    top: 50px !important;
/*     left: -465% !important; */
    width: 100% !important;
}

.chat-list > div:nth-child(1) > div:nth-child(2){
    height: 640px !important;
}

.chat-input{
    height: 50% !important;
    position: absolute !important;
    top: 5px !important;
}

.tw-animated-number{
    text-shadow: 2px 2px #000 !important;  
}




.player-controls__right-control-group{
    position: absolute !important;
    left: 50% !important;
}

.chat-line__message, .chat-line__moderation, .chat-line__status{ font-size: 18px}
}

@-moz-document regexp("https://www.twitch.tv/.*") {
.right-column--theatre {
    background-color: transparent !important;
}

.channel-root {
    background-color: rgba(210, 0, 0, 0) !important;
}

.video-player__container--theatre-whispers {
    bottom: 0.25rem;
}

.chat-line__message{
    opacity: 90% !important;
    background-color: rgba(255, 255, 255, 0) !important;
    text-shadow: 2px 2px #000;
}

.tw-root--theme-dark .chat-room {
     background-color: rgba(255, 255, 255, 0) !important;
}

.tw-c-background-base {
    background-color: rgba(255, 255, 255, 0) !important;
}

.tw-root--theme-dark, :root {
--border-radius-large: unset !important;
--border-radius-medium: unset !important;
--border-radius-none: unset !important;
--border-radius-rounded: unset !important;
--border-radius-small: unset !important;
--border-width-default: unset !important;
--border-width-input: unset !important;
--border-width-input-overlay-unfocused: unset !important;
--border-width-marked: unset !important;
--border-width-spinner: unset !important;
--border-width-tag: unset !important;
}

.persistent-player {
top: 0px !important;
left: -.25vw !important;
position: fixed !important;
width: 100% !important;
max-height: 100% !important;
height: 100% !important;;
transform: scale(1.015) !important;;
}


.stream-chat-header{
    display: none !important;
}

.channel-root + .persistent-player {
    width: 200%;
}

.right-column__toggle-visibility{
    display: none !important;
}

.channel-leaderboard{
    display: none !important;
}

.community-highlight-stack__scroll-area--disable{
    display: none !important;
}

.tw-mg-l-05{
    display: none !important;
}


.theatre-social-panel{
    position: absolute !important;
    left: 50% !important;
}


.community-points-summary{
    position: absolute !important;
    top: 51vw !important;
    left: 200px !important;
}


.tw-align-content-center > div:nth-child(2){
    position: absolute !important;
    top: 193.5% !important;
    right: 18vw !important;
}


.chat-settings__content{
   background-color:#26262a!important;
}

.tw-block{
    background-color: transparent !important;
}

}
