css:
@-moz-document domain("www.nitrotype.com")
{
  	body
	{
	  background-color: #2d2e2e;
	  overflow-y: scroll;
	  background: none;
	  overflow-x: hidden;
	  position: relative;
	}
	.card--grit
	{
 	 background-image: url("https://i.imgur.com/6KSYNtB.png");
 	 background: scroll;
	}

	.news-teaser
	{
  	color: #fff;
	}

	.flash--beta
	{
 	 display: none;
	}

	.theme--pDefault .profile-bg
	{
 	 background: url("https://live.staticflickr.com/1901/45574031691_c042f9794c_b.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
 	 color: #d3d3d3;
 	 background-size: 100%
	}

	.theme--pDefault .profile-bgHead
	{
	  background: url("https://live.staticflickr.com/1901/45574031691_c042f9794c_b.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
 	 background-size: 100%
	}

	.theme--pDefault.is-gold .profile-bg
	{
	  background: url("https://live.staticflickr.com/1901/45574031691_c042f9794c_b.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #f00 100%);
 	 background-size: 100%
	}

	.card--a
	{
	  background-color: #fff0;
 	 color: #0ff;
	}
	.card--c
	{
 	 background-color: #fff0;
 	 color: #0ff;
	}

	.card--shadow-l
	{
	  box-shadow: none;
	}
	.theme--pDefault .profile-levelNum {
 	 color: #0ff;
 	 text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
	}
	.theme--pDefault .profile-title {
	  color: #0ff;
	  text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
	}
	.theme--pDefault .profile-head {
 	 color: #0ff;
	  text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
	}
	.btn--inky, .btn--inky:focus:not(:hover) {
	  color: #0ff;
	}
	.btn--primary
	{
 	 background: #0009ff;
 	 color: #0ff;
	}

	.card--b {
	  color: #0ff;
	}
	.theme--pDefault .profile-body {
 	 color: #0ff;
	}

	html
	{
 	 background: fixed url("https://i.imgur.com/6KSYNtB.png") no-repeat, #2d2e2e;
 	 box-sizing: border-box;
 	 color: #2d2e2e;
	  font-family: "Montserrat", sans-serif;
 	 font-size: 16px;
 	 font-weight: 300;
 	 line-height: 1.6;
	  background-size: 100%
	}

	.structure-ad
	{
	  display: none;
	}

	.ad
	{
 	 display: none;
  	position: relative;
	  margin-right: auto;
 	 margin-left: auto;
 	 text-align: center;
	}

	.ad--sq
	{
	  display: none;
  	  width: 336px;
 	  height: 280px;
 	 background: #f2f2f2;
	}

    .ad--ldrbrd
    {
      display: none;
      width: 728px;
      height: 90px;
      background: #f2f2f2;
    }

    .ad--ldrbrd_l
    {
      display: none;
      width: 970px;
      height: 90px;
    }

    .ad--fixed
    {
      display: none;
      position: fixed;
      top: 30px;
    }

    .structure-header
    {
      margin-left: auto;
      margin-right: auto;
      max-width: 1020px;
      padding-left: 0px;
      padding-right: 0px;
    }



    .table--b .table-cell
    {
      border-bottom: none;
    }

    .tc-ts
    {
      color: #fff;
    }
   .tc-t {
    color: #0ff;
   }

    .card--b
    {
      color: #fff;
    }

    .tc-emerald
    {
      color: #fff;
    }

    .tc-i
    {
      color: #fff;
    }

    .tc-dodger
    {
      color: #0ff;
    }

    .tc-lemon
    {
      color: #ffcb00;
    }

    .news--solo .news-body p
    {
      color: #0ff;
    }

    .news--solo .news-body .yellow
    {
      color: #fff600;
    }

    .link--nav
    {
      color: #0ff;
    }

    .link--nav.is-current
    {
      color: #000;
    }

    .link--nav:hover
    {
      color: #000;
    }

    .bg--gradient
    {
      background: #00000000;
    }

    path:nth-child(2)
    {
      fill: #7300ff;
    }

    path:nth-child(3)
    {
      fill: #000dff;
    }

    path:nth-child(4)
    {
      fill: #0063fb;
    }

    path:nth-child(5)
    {
      fill: #0084ff;
    }

    path:nth-child(6)
    {
      fill: #0ff;
    }

    path:nth-child(8)
    {
      fill: #00ff8c;
    }
    .dash-copyContainer
    {
      background: #1d1e23;
      -webkit-box-shadow: 2px 1px 157px 10px rgb(255, 0, 0);
      box-shadow: 0px 1px 157px 10px rgb(0, 179, 255);
      border-radius: 5px;
      flex: 1;
      overflow: hidden;
      padding: 15px;
      display: flex;
    }

    .dash-copy
    {
      color: #0ff;
      text-shadow: 0 0 5px #00ffec, 0 0 7px #0059ff;
      font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
      font-size: 19px;

    }
    ::selection
    {
      background-color: #1976c6;
      color: white;
    }
    body
    {
      background-color: #fff;
      overflow-y: scroll;
      background: none;
      overflow-x: hidden;
      position: relative;
    }

    html
    {
      background: fixed url("https://i.imgur.com/6KSYNtB.png") no-repeat, #6800ff;
      box-sizing: border-box;
      color: #ec00ff;
      font-family: "Montserrat", sans-serif;
      font-size: 16px;
      font-weight: 300;
      line-height: 1.6;
      background-size: 105%
	}
}
