css:
@-moz-document domain("getslowly.com") {
body, header {
    background-color: #000 !important;
    color: #eee;
}

a {
    color: #2196F3 !important;
}

table {
    color: #eee !important;
}

header i {
    color:#eee !important;
}


.text-lighter, .text-darker {
    color: #fff !important;
}

.text-lighter {
    color: #343434 !important;
}

.text-primary, .text-positive {
    color: #2196F3 !important;
}

.text-instagram {
    color: #CC0099;
}

.text-black-50, .badge {
    color: #eee !important;
}

header a img, .modal-body[dir="ltr"] > div:first-child > div {
    filter: invert(1);
}


.nav-link {
    color: #ffc300 !important;
}

.card > .overflow > div > a > div > div > div .col-auto .text-lighter {
    color: #eee !important;
}

.card, card > .overflow > div, .card > .overflow > div > a, .card > .overflow > div > a * {
    background-color: #212121;
}

.card > .overflow {
    background-color: #212121  !important;
    color: #1976D2;
}

.card > .overflow > div > a p {
    background-color: #212121  !important;
    color: #1976D2 !important;
}

.card > .overflow > div > a h6, .letter-sm:hover .card-text {
    color: #1976D2 !important;
}

.card > .overflow > div > a.selected, .card > .overflow > div > a.selected * {
    background-color: #343434  !important;
    color: #1976D2 !important;
}
.card > .overflow > div > a:hover > *,
.card > .overflow > div > a:hover > div > *,
.card > .overflow > div > a:hover h6,
.list-group-item:hover:not(.active) {
    background-color: #212121  !important;
    color: #2196F3 !important;
}


.card > .overflow > div > a:hover,
.card > .overflow > div > a:hover > div,
.card > .overflow > div > a:hover > div > div,
.card > .overflow > div > a:hover > div > div > div,
.card > .overflow > div > a:hover > div > div > div .col-auto,
.card > .overflow > div > a:hover > div > div > div .col-auto .text-lighter,
.card > .overflow > div > a:hover i,
.card > .overflow > div > a:hover,
.card > .overflow > div > a:hover > *,
.card > .overflow > div > a:hover > div > *,
.card > .overflow > div > a:hover h6 {
    background-color: #212121  !important;
}

.card-header {
    background-color: #212121 !important;
    color: #2196F3 !important;
}

.dropdown-menu, .popover, .list-group, .modal-header, .modal-content , .editor .modal-header {
    background-color: #212121 !important;
}

.dropdown-item, .list-group-item, .card-text, .modal-footer {
    color: #eee;
    background-color: #343434 !important;
}

.dropdown-item.active, .list-group-item.active {
    background-color: #1976D2 !important;
}

textarea, .letter-sm .card-text, .letter-sm .card-text:after {
    background: none;
    background-color: #343434 !important;
    color: #fff !important;
}

.main-container  div {
    background-color: #343434 !important;
}

.editor .modal-header button {
 color: #eee;     
}

.editor .modal-header button:hover {
 color: #eee;     
    border: 1px solid white;
}

.editor {
    background-color: transparent !important;
    border: none !important;
}

.focus-mode .editor-wrapper {
    background-color: rgba(0,0,0, .9) !important;
}
}
