/* ==UserStyle==
@name        Floatplane Dark theme
@namespace   nejento.eu
@author      nejento (https://github.com/nejento, https://twitter.com/nejento)
@version     1.0.0
@license     MIT license
@homepageURL  http://example.com/
==/UserStyle== */

@-moz-document domain("floatplane.com") {
    /* Floatplane Dark theme (or Darkplane) */
    /* Author: nejento (Twitter: @nejento, Discord: nejento#5612, Website: nejento[dot]eu) */
    
    /* Shoutout to whole Floatplane team for a great paid early access content! */
    /* This theme can be considered as a temporary dark theme until Floatplane gets its own dark theme */
    
    /* If there are any problems with the theme, you may try to contact me via private messages on Twitter, LinusTechTips forums or on Discord */
    
    /* !important flag is implemented due to _ngcontent attribues which are added during compilation of the website: [_ngcontent-c14] */
    /* The !important flag allows to apply the CSS rule over the CSS rules given with selectors with attributes attached to them */
    
    /* ---- */
    /* Page */
    /* ---- */
    /* Links */
    a {
        color: #5886bd;
    }
    
    body {
        color: #eee;
    }
    
    /* Page background */
    .page-wrapper .route-wrapper {
        background-color: #0e0e10;
    }
    
    /* ------- */
    /* Sidebar */
    /* ------- */
    /* Sidebar */
    .sidebar {
        background: #000;
    }
    
    /* Dropup */
    .sidebar .sb-hovergroup > .subscription-item > .dropup {
        background: #222;
    }
    
    /* -------- */
    /* Homepage */
    /* -------- */
    .tabs-wrapper .nav {
        background: #0e0e10;
        box-shadow: inset 0 -1px 0 #333, -1px 0 0 #0e0e10, 1px 0 0 #0e0e10;
    }
    
    /* ---------------- */
    /* Channel homepage */
    /* ---------------- */
    /* Header */
    .channel-intro-wrapper .channel-intro-content .channel-intro-width-wrapper .channel-nav {
        background: #0e0e10;
        box-shadow: inset 0 -1px 0 #333, -1px 0 0 #0e0e10, 1px 0 0 #0e0e10;
    }
    
    /* Header transition */
    .channel-card .channel-card-header .channel-bg-wrapper .channel-bg::after,
    .channel-intro-wrapper .channel-intro-bg-wrapper::after,
    .page-wrapper .hero-wrapper .hero .hero-background::after {
        background: linear-gradient(to top, rgba(14, 14, 16, 0.6) 0%, rgba(14, 14, 16, 0) 75%) !important;
    }
    
    /* Header hover */
    .channel-intro-wrapper .channel-intro-content .channel-intro-width-wrapper .channel-nav .channel-nav-item:hover,
    .tabs-wrapper .nav .nav-item:hover {
        background: rgba(255, 255, 255, 0.05);
        color: #0094c8;
    }
    
    /* Active button in header */
    .channel-intro-wrapper .channel-intro-content .channel-intro-width-wrapper .channel-nav .channel-nav-item.active,
    .tabs-wrapper .nav .nav-item.active {
        color: #fff;
    }
    
    /* Header search */
    .channel-search .search-bar.focus {
        background: #ffffff0a !important;
        box-shadow: inset 0 -1px 0 #444 !important;
        border-left: none !important;
    }
    
    /* Video titles */
    .info-box .title,
    .video-content-wrapper .video-list .video-list-item {
        color: #eee !important
    }
    
    /* Channel description */
    .markdown-body {
        color: #eee;
    }
    
    /* Channel description underline */
    .markdown-body h1,
    .markdown-body h2 {
        border-bottom: 1px solid #555;
    }
    
    /* Subbox */
    .subBox {
        background-color: #222 !important;
    }
    
    .subBox.sharp {
        box-shadow: inset 0 -1px 0 #333, -1px 0 0 #333, 1px 0 0 #333 !important;
    }
    
    .subBox .header .price,
    .subBox .header .sub-count {
        color: #888 !important;
    }
    
    /* Modal */
    .modal-content {
        background-color: #222;
    }
    .subBox.feather {
        border: 1px solid #333 !important;
    }
    
    /* Channel name in modal */
    .creatorMain .creatorCard .creatorVital .creatorTitle {
        color: #fff !important;
    }
    
    /* ----- */
    /* Video */
    /* ----- */
    /* Video list */
    .video-content-wrapper .video-list .video-list-item + .video-list-item {
        border-top: 1px solid #333;
    }
    
    /* Comments input box */
    .video-content-wrapper .textarea-wrapper textarea {
        background: #333;
        border: 1px solid #333;
    }
    
    /* Comments authors */
    .video-content-wrapper .comments-wrapper .comment .comment-header .header-text .author,
    .video-content-wrapper .comments-wrapper .comment .reply-header .header-text .author,
    .video-content-wrapper .comments-wrapper .reply .comment-header .header-text .author,
    .video-content-wrapper .comments-wrapper .reply .reply-header .header-text .author,
    .video-content-wrapper .replies-wrapper .comment .comment-header .header-text .author,
    .video-content-wrapper .replies-wrapper .comment .reply-header .header-text .author,
    .video-content-wrapper .replies-wrapper .reply .comment-header .header-text .author,
    .video-content-wrapper .replies-wrapper .reply .reply-header .header-text .author {
        color: #aaa;
    }
    
    /* Download dropdown */
    .download-container .dropdown {
        background: #333 !important;
    }
    
    .download-container .dropdown .dropdown-item {
        color: #ccc !important;
    }
    
    /* ------------ */
    /* Descriptions */
    /* ------------ */
    /* Description box */
    .generic-wrapper .generic-block,
    .generic-wrapper .generic-block-padded,
    .generic-wrapper .generic-block-padded-threeway {
        background: #222;
        border: 1px solid #333;
    }
    
    /* Title */
    .video-content-wrapper .video-description-wrapper .video-description-header .video-title {
        color: #fff;
    }
    
    /* Subtitle */
    .video-content-wrapper .video-description-wrapper .video-description-header .channel-info .channel-title {
        color: #eee;
    }
    
    /* Links in descriptions */
    .video-content-wrapper .video-description-wrapper .video-description a {
        color: #5886bd;
    }
    
    /* Underline */
    .video-content-wrapper .video-description-wrapper .video-description-header {
        border-bottom: 1px solid #444;
    }
    
    /* Gradient for readmore */
    .video-content-wrapper .video-description-wrapper .video-description::after {
        background: linear-gradient(to top, #333 0%, rgba(255, 251, 251, 0) 70%);
    }
    
    /* Show more button */
    .video-content-wrapper .video-description-wrapper .expand-description {
        background: #444;
    }
    
    /* ------------- */
    /* User activity */
    /* ------------- */
    .activity-feed .activity-feed-period .feed-list {
        color: #bbb !important;
    }
    
    /* Links */
    .activity-feed .activity-feed-period .feed-list .feed-item .item-caption a {
        color: #5886bd !important;
    }
    
    /* ------------- */
    /* User settings */
    /* ------------- */
    .cms-layout .cms-pane,
    .cms-layout .settings-pane,
    .settings-layout-right .settings-pane {
        background: #222;
        border: 1px solid #333;
    }
    
    .cms-layout .cms-pane .col-section > .row-section,
    .cms-layout .settings-pane .col-section > .row-section,
    .settings-layout-right .settings-pane .col-section > .row-section {
        border-bottom: 1px solid #333;
    }
    
    .cms-layout .cms-pane .col-section,
    .cms-layout .settings-pane .col-section,
    .settings-layout-right .settings-pane .col-section {
        border-left: 1px solid #333;
        border-bottom: 1px solid #333;
    }
    
    .cms-layout .cms-pane .col-section > .row-section > .row-title.subtitle,
    .cms-layout .settings-pane .col-section > .row-section > .row-title.subtitle,
    .settings-layout-right .settings-pane .col-section > .row-section > .row-title.subtitle,
    .cms-layout .cms-pane .col-section > .row-section > .row-title,
    .cms-layout .settings-pane .col-section > .row-section > .row-title,
    .settings-layout-right .settings-pane .col-section > .row-section > .row-title {
        color: #fff;
    }
    
    .settings-general .username,
    .stats-group > .stat-item > .value {
        color: #dff7ff !important;
    }
    
    .cms-layout .cms-pane .vertical-list.alternate-rows .list-item:nth-child(2n),
    .cms-layout .settings-pane .vertical-list.alternate-rows .list-item:nth-child(2n),
    .settings-layout-right .settings-pane .vertical-list.alternate-rows .list-item:nth-child(2n) {
        background: #252525;
    }
    
    .auth-group-section {
        border-bottom: 1px solid #444 !important;
    }
    
    .support-category:not(:last-child) {
        border-bottom: 1px solid #333 !important;
    }
    
    .settings-general {
        border-bottom: 1px solid #444 !important;
    }
    
    /* Invoice */
    .invoice:not(:last-child) {
        border-bottom: 1px solid #444 !important;
    }
    
    .invoice .invoice-content .invoice-sub-container {
        border: 1px solid #444 !important;
    }
    
    .invoice .invoice-content span.invoice-light {
        color: hsla(0, 0%, 76.9%, 0.75) !important;
    }
    
    .invoice .invoice-content {
        color: #eee !important;
    }
    
    /* Menu */
    .settings-layout-left .navlist {
        background: #000;
        border: 1px solid #333;
    }
    
    .settings-layout-left .navlist > .navlist-item:not(:first-of-type) {
        border-top: 1px solid #333;
    }
    
    /* Headers */
    .white-background {
        background: #333 !important;
    }
    
    .cms-layout .cms-pane .col-section.darker,
    .cms-layout .settings-pane .col-section.darker,
    .settings-layout-right .settings-pane .col-section.darker {
        background: #444;
    }
    
    .cms-layout .cms-pane .col-section > .row-section p.light,
    .cms-layout .settings-pane .col-section > .row-section p.light,
    .settings-layout-right .settings-pane .col-section > .row-section p.light,
    .cms-layout .cms-pane .col-section > .row-section p,
    .cms-layout .settings-pane .col-section > .row-section p,
    .settings-layout-right .settings-pane .col-section > .row-section p {
        color: #eee;
    }
    
    .cms-layout .cms-pane .col-section > .row-section,
    .cms-layout .settings-pane .col-section > .row-section,
    .settings-layout-right .settings-pane .col-section > .row-section {
        color: #fff;
    }
    
    /* Inputs */
    .cms-layout .cms-pane .input-group textarea,
    .cms-layout .cms-pane .input-group > input,
    .cms-layout .settings-pane .input-group textarea,
    .cms-layout .settings-pane .input-group > input,
    .settings-layout-right .settings-pane .input-group textarea,
    .settings-layout-right .settings-pane .input-group > input {
        color: #eee;
        background: #333;
        border: 1px solid #444;
    }
    
    .cms-layout .cms-pane .dropdown-container > select,
    .cms-layout .cms-pane .input-group > select,
    .cms-layout .settings-pane .dropdown-container > select,
    .cms-layout .settings-pane .input-group > select,
    .settings-layout-right .settings-pane .dropdown-container > select,
    .settings-layout-right .settings-pane .input-group > select {
        color: #eee;
        background: #333;
        border: 1px solid #444;
    }
    
    .cms-layout .cms-pane .input-group .custom-text + label,
    .cms-layout .cms-pane .input-group select + label,
    .cms-layout .cms-pane .input-group textarea + label,
    .cms-layout .cms-pane .input-group > input + label,
    .cms-layout .settings-pane .input-group .custom-text + label,
    .cms-layout .settings-pane .input-group select + label,
    .cms-layout .settings-pane .input-group textarea + label,
    .cms-layout .settings-pane .input-group > input + label,
    .settings-layout-right .settings-pane .input-group .custom-text + label,
    .settings-layout-right .settings-pane .input-group select + label,
    .settings-layout-right .settings-pane .input-group textarea + label,
    .settings-layout-right .settings-pane .input-group > input + label {
        color: #ddd;
    }
    
    /* -------------------- */
    /* Browse creators page */
    /* -------------------- */
    /* Search bar */
    .creator-search .search-bar {
        border: 1px solid #333 !important;
        background: #222 !important;
        color: #eee !important;
    }
    
    /* Creator card */
    .creator-cards figure {
        background-color: #333 !important;
        border-radius: 5px !important;
    }
    
    .creator-cards figure .boxWrap img {
        height: 100% !important;
        width: 100% !important;
        margin-left: 0 !important;
        margin-top: 0 !important;
        border-radius: 5px 5px 0 0 !important;
    }
    
    /* Channel name */
    #wrapper-part-info {
        background-color: #222 !important;
        border-radius: 0 0 5px 5px !important;
    }
    
    /* Channel name */
    #part-info {
        color: #ddd !important;
    }
    
    /* ---- */
    /* Chat */
    /* ---- */
    /* Chat background */
    live-chat.ng-star-inserted {
        background: #111;
        border: 1px solid #333;
    }
    
    /* Chat options */
    /*.chat-messages-options[_ngcontent-c5],*/
    .chat-messages-options {
        background-color: #333 !important;
    }
    
    /* Username */
    /*.chat-messages-container[_ngcontent-c5] .message-list[_ngcontent-c5] .chat-message[_ngcontent-c5] .chat-username[_ngcontent-c5],*/
    .chat-messages-container .message-list .chat-message .chat-username {
        color: #aaa !important;
    }
    
    /* Chat message container */
    /*.chat-messages-container[_ngcontent-c5] .message-list[_ngcontent-c5] .chat-message[_ngcontent-c5],*/
    .chat-messages-container .message-list .chat-message {
        border-bottom: 1px solid #333 !important;
    }
    
    /* Chat message container - Moderator, Creator */
    /*.chat-messages-container[_ngcontent-c5] .message-list[_ngcontent-c5] .chat-message.type-Creator[_ngcontent-c5],
    .chat-messages-container[_ngcontent-c5] .message-list[_ngcontent-c5] .chat-message.type-Moderator[_ngcontent-c5],*/
    .chat-messages-container .message-list .chat-message.type-Creator,
    .chat-messages-container .message-list .chat-message.type-Moderator {
        background: #1e2833 !important;
        border-bottom: 1px solid #333 !important;
    }
    
    /* Chat input */
    /*.chat-messages-container[_ngcontent-c5] .chat-entry-container[_ngcontent-c5],*/
    .chat-messages-container .chat-entry-container {
        background-color: #333 !important;
        border-top: 1px solid #333 !important;
    }
    
    /*.chat-messages-container[_ngcontent-c5] .chat-entry-container[_ngcontent-c5] .chat-input[_ngcontent-c5],*/
    .chat-messages-container .chat-entry-container .chat-input {
        background-color: #333 !important;
    }
    
    /* ------ */
    /* Player */
    /* ------ */
    .video-player-wrapper {
        background: #0e0e10;
    }
    
    /* -------- */
    /* Old code */
    /* -------- */
    .generic-block-padded {
        background-color: #242424;
    }
    
    .channel-info,
    .channel-title {
        color: #333;
    }
    
    /*
    .video-content-wrapper .video-description-wrapper .video-description-header .video-title, .video-content-wrapper .video-description-wrapper .video-description-header .channel-info, .channel-title {
      color: #fff;
    }
    */
    }
