css:
@-moz-document url-prefix("https://lichess.org/simul/") {
    .game-list {
        margin-top: 450px !important;
        position: relative;
    }
    .host {
        padding: 0 !important;
        position: absolute;
        top: -400px;
        left: 50%;
        transform: translateX(-50%);
        width: 304px; /* multiple of 8 */
        height: auto;
        box-shadow: 0 0 0 7px rgba(214,79,0,0.5) !important;
    }
}
