css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("99chan.org") {
html, body {
    font-size:12pt;
    background:#000000;
    color:#999999;
    font:10pt "Trebuchet MS",Trebuchet,serif;
}

a {
    background:inherit;
    color:#3399FF;
    text-decoration:none !important;
    font-family:"Trebuchet MS",Trebuchet,serif;
    font:13px Trebuchet MS !important;
}

a:visited {
    background:inherit;
    color:#3399FF;
    text-decoration:none;
    font-family:"Trebuchet MS",Trebuchet,serif;
}

a:hover {
    color:#DD0000;
    background:inherit;
    font-family:"Trebuchet MS",Trebuchet,serif;
    font:13px Trebuchet MS !important;
    font-weight:normal !important;
    text-decoration:underline !important;
}

.filesize a, .filesize a:visited {
    text-decoration:underline;
}

.adminbar {
    text-align:right;
    background:inherit;
    clear:both;
    float:right;
}

.logo {
    clear:both;
    text-align:center;
    background:inherit;
    font-size:24pt;
    color:#FF66FF;
    width:100%;
}

.replymode {
    background:#FF9966;
    color:#000000;
    width:100%;
}

.catalogmode {
    background:#999999;
    color:#000000;
    width:100%;
}

.postarea {
    background:#000000;
}

.rules {
    width:468px;
    font-size:10px;
    font-family:"Trebuchet MS",Trebuchet,serif;
}

.rules li {
    margin-left:1em;
}

.postblock {
    background:#333333;
    color:#FFFFFF;
    font-weight:800;
    font-family:"Trebuchet MS",Trebuchet,serif;
}

.footer {
    text-align:center;
    font-size:10px;
    font-family:"Trebuchet MS",Trebuchet,serif;
}

.passvalid {
    background:#9988EE;
    text-align:center;
    width:100%;
    color:#ffffff;
}

.dellist {
    background:inherit;
    text-align:center;
}

.delbuttons {
    background:inherit;
    text-align:center;
    padding-bottom:4px;
}

.managehead {
    background:#0F8FE1;
    color:#000000;
    font-family:"Trebuchet MS",Trebuchet,serif;
    font-size:14px;
    padding:0px;
}

.postlists {
    background:#000000;
    width:100%;
    padding:0px;
    color:#999999;
}

.row1 {
    background:#9AD2F6;
    font-family:"Trebuchet MS",Trebuchet,serif;
    font-size:12px;
    color:#000000;
}

.row2 {
    background:#FFFFFF;
    font-family:"Trebuchet MS",Trebuchet,serif;
    font-size:12px;
    color:#000000;
}

.unkfunc {
    color:#000000;
}

.filesize {
    font-size:12px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    text-decoration:underline;
    padding-left:3em;
}

.filetitle {
    background:inherit;
    font-size:18px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#0F0C5D;
    font-weight:800;
}

.postername, .commentpostername {
    background:inherit;
    font-size:12px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#117743;
    font-weight:800;
}

.oldpost {
    background:inherit;
    font-size:18px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#FFFFFF;
    font-weight:800;
}

.omittedposts {
    background:inherit;
    font-size:14px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#C00;
    font-weight:800;
}

.reply {
    -moz-border-radius:8px;
    -webkit-border-radius:8px;
    -moz-border-radius-topleft:0px;
    -webkit-border-radius-topleft:0px;
    background:#333333;
    color:#000000;
    font-family:"Trebuchet MS",Trebuchet,serif;
}

.replyhl {
    background:#D6BAD0;
    color:#000000;
}

.doubledash {
    vertical-align:top;
    clear:both;
    float:left;
}

.replytitle {
    background:inherit;
    font-size:18px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#FF0000;
    font-weight:800;
}

.thumbnailmsg {
    background:inherit;
    font-size:9px;
    font-family:"Trebuchet MS",Trebuchet,serif;
    color:#000000;
}
.watchedthreads {
    background-color:#D6DAF0 !important;
    border:1px dotted #D6DAF0 !important;
    border-top:0px none !important;
}

.reflinkpreview {
    background-color:#D6DAF0 !important;
}

textarea {
    font:10pt "Trebuchet MS", Arial, Helvetica, sans-serif;
    margin-right:2px !important;
    height:300px !important;
}

@namespace url(http://www.w3.org/1999/xhtml);  body, span#navtop, td.replyhl, td.reply, a.quotejs, a.quotelink, span.filesize {
    font:13px Trebuchet MS !important;
}

a.quotejs:hover {
    font:13px Trebuchet MS !important;
    font-weight:normal !important;
    text-decoration:underline !important;
}

iframe, td.rules{
    display:none !important;
}

hr {
    display:none !important;
    height:1px !important;
    border:none !important;
    background:#999 !important;
}

form hr {
    display:block !important;
}

table, td {
    border:none !important;
}

input {
    margin-right:2px !important;
}

div.postarea {
    position:fixed !important;
    width:472px !important;
    height:0px !important;
    opacity:0.2 !important;
    right:0px !important;
        top:40px !important;
}

div.postarea:hover {
    opacity:1 !important;
}

td.postblock {
    vertical-align:top !important;
    font-size:10px !important;
}
}
