css:
.panel-default > .panel-heading, .konviva-body, .panel, body:not(.k-has-menu-admin) .konviva-content-full-letter .konviva-content-full, .k-footer-inner, body:not(.k-has-menu-admin) #playerMainDiv.player-body .konviva-comments-wrap, .player-main-content, .comment-textarea, .konviva-comments-wrap .reply,.reply-textarea, .form-control, .k-page-view-fullscreen .nav, .k-info-curso-turma-sala, .k-control-header-navega, .k-lista-conteudo-icones, body:not(.k-has-menu-admin) #playerMainDiv.player-body .dropdown-descricao, .konviva-comments-wrap .konviva-comment-box, .k-content-login , .portal, .portal-navbar, .bg-portal-login{
background: #000 !important;
}

.k-corfundoplayer-background{background: rgb(8, 8, 8);}

.k-back-menu-sala {background: #050505;}

.k-back-menu-sala .bs-glyphicons li.active , .portal-header .portal-navbar-nav li.active>a, .nav-tabs > li.active > a:hover, .konviva-menu-vertical > .ng-scope:hover{background: #ffffff0a !important;}

.k-ferramentas-aula, .nav-tabs > li.active > a{background: #ffffff0d;}

.panel-default, .k-info-curso-turma-sala, .konviva-content-list-conteudo .panel-body .k-content-item-tree:first-child:before, .konviva-content-list-conteudo .panel-body .k-content-item-tree, .konviva-content-list-conteudo .panel-body .k-content-item-tree:nth-child(n) .nivel2:first-child:before, .konviva-content-list-conteudo .panel-body .k-content-item-tree:nth-child(n) .nivel2:nth-child(n) .k-content-item-tree-organizer:after, .k-lista-conteudo-icones:before, .k-lista-conteudo-icones:after, .k-back-menu-sala .bs-glyphicons li.active{border-color: #dddddd1f !important;}


/*.ng-scope > .konviva-comments-wrap{border-top:1px solid gray;}*/
.form-control{border: 1px solid #111;}

.generalbox, .player-main-content, .btn-default, .help-block, .nav-tabs > li.active > a:hover, .konviva-color-666:hover, .konviva-menu-vertical > .ng-scope:hover, .konviva-content-list-conteudo .panel-heading h4{color: rgb(185, 183, 183) !important;}
.portal-navbar-nav, body.konviva-body{color:#fff !important;}

#avaliacaoSalaAula .media-body ,#avaliacaoSalaAula .k-content-alternative ,#avaliacaoSalaAula .k-num-question, .btn-default, .k-page-view-fullscreen .icon{background: #0c0c0c;}
#avaliacaoSalaAula .media-body, .btn-default , .k-ferramentas-aula {border-color: #0c0c0c;}
#avaliacaoSalaAula .k-arrow-right:after {border-color: transparent #0c0c0c transparent transparent;}
.btn-default:hover{background: #0f0f0f;border-color: #0c0c0c;}

.k-tpl-avaliacao .k-header-avaliacao-new .k-info-header-avaliacao ,nav.navbar-player-tools ul.nav, .k-footer-inner {border-color: #000000 !important;}

.k-page-view-fullscreen .nav{background: #151515 !important;}

