css:
@-moz-document domain("carloscuesta.me") {
.wrap { max-width: 1200px }
main.wrap { padding-left: .2rem; padding-right: .2rem }

/* .row { display: block }
@media (min-width: 580px) { .row { column-count: 2 }}
@media (min-width: 980px) { .row { column-count: 3 }} */
.row { margin-left: 0; margin-right: 0 }

.emoji {
    display: inline-block; /* Not `block` to avoid separating */
    padding: 0;
/*     max-width: none;
    width: 100%; */
}
.emoji .gitmoji-emoji {
    margin: .15rem 0 0 -.15rem;
    font-size: 2.5em;
    pointer-events: none; /* To select and avoid to auto copy */
}

.emoji-card {
    display: flex;
    margin: 0 .2rem .4rem;
    box-shadow: none;
    border: solid 1px #0001;
    min-height: 4.4rem;
    text-align: left;

    font-size: .85rem;
    line-height: 1.15384615;
    
    transition: none;
}
.emoji-card:hover {
    box-shadow: none;
    transform: none;
}

.emoji-header {
    align-self: unset;

    display: flex;
    align-items: center;
    border-radius: 0;
    padding: 0 .75rem;
    background: #f9f9f9;
}

.emoji-info {
    align-self: center;
    padding: .6rem;
}
.emoji-info code {
    display: block;
    max-width: calc(100% - .6rem * 2);
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
    font-size: .9rem;
    line-height: 1;
    opacity: .2;
}
.emoji-info p {
    margin: .25rem 0 0;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
/*     height: 2em; */
    overflow: hidden;
}
}
