/*cmr.cl Dark Style*/
@-moz-document domain("cmr.cl"), domain("bancofalabella.cl") {
/* CSS para pagina principal de cmr.cl*/
 body {
    background-color: #000;
    color: #54a9ff;
}
.bg-transparent {
    background-color: #000000c7 !important;
}
.bg-transparency {
    background-color: #000;
}
.main-header, #fixed-top-container {
    background-color: #3a3a3a;
    color: #54a9ff;
}
.nav-light .nav-link, .nav-light .show>.nav-link {
    color: #00bd17;
}
.nav-light .nav-link.active, .nav-light .nav-link:hover {
    color: #00ff00;
}
.dropdown-menu, .dropdown-item {
    background-color:#000;
    color: #00bd17;
}
.submenu {
    background-color:#3a3a3a;
    color: #00bd17;
}
.dropdown-item:hover {
    background-color: #3a3a3a;
    color: #00ff00;
}
.has-submenu.show, .has-submenu:hover {
    background-color: #3a3a3a;
}
.nav-segmented .nav-item:not(:last-child)::after {
    background-color: #fb8600;
}
.nav-light.show>.nav-link {
    color: #00bd17;
}
#templateRow0, #templateRow3 {
    background-color: #333;
}
#templateRow2 {
    background: #000;
}
#HeaderFalabellaAccessOtherSites {
    background-color: #333;
    color: #00ff00;
}
.float-field_nobox_nolabel_consolidada, .html-code_consolidada, .headerFalabellaAccessOtherSitesContainer a {
    color: #d8d8d8;
}
#cyberbankBody, element.style {
    color: #05d205;
    border-left: solid 2px #ff7101;
}
#headerFalabellaCMRCHAvatar .lastAccessDate {
    color: #0f0;
}
.dynamic-label_h5-regular {
    color: #fff;
}
.dynamic-label_consolidada, .constant-label_h5_regular-link {
    color: #a1a1a1;
}
.dynamic-label_h6-light {
    color: #777;
}
.constant-label_h4_regular-float-right {
    color: #9fa0a0;
}
.table-align_wrap-fullwidth-border-left, .table-align_wrap-fullwidth-hover {
    background-color: #1d1d1d;
}
}

@-moz-document url("https://lia.fif.tech/falachat/Index.html") {
/* CSS para el chat de Lia*/
    body{
		background-color: #3f3f3f;
		color: #ccc;
	}
	
	#frame .content .messages ul li.sent p {
		background: #151515;
		color: #00d056;
		border: solid 1px #c2ffc8;
	}
	
	.textarea, #frame .content .message-input .wrap {
		background: #000;
		color: #ccc;
	}
  
  #frame .content .message-input .wrap button:hover {
        background: #000;        
        color: #0f0;
    }
    
    #frame .content .message-input .wrap button {
        background: #000;
        color: #029640;
    }
}
