css:
@-moz-document url-prefix("https://www.wwe.com") {
body {
    background-color:#0a0a0a !important;
}
.superstars-champions--headline--highlight {
    font-weight: bold;
    color: #fff;
}
.superstars--headline--highlight {
    font-weight: bold;
    color: #fff;
}
html {
    color: #fff;
}
.wwe-feed-cards--title h2, .node-type-poll .wwe-feed-cards--card__poll-interactive .wwe-feed-cards--thanks-title h2, .wwe-feed-cards--title h3, .node-type-poll .wwe-feed-cards--card__poll-interactive .wwe-feed-cards--thanks-title h3, .wwe-feed-cards--title a, .node-type-poll .wwe-feed-cards--card__poll-interactive .wwe-feed-cards--thanks-title a {
    color: #fff;
}
.wwe-article__hero {
    background: #0a0a0a
}
.wwe-article__hero--title {
    color: #fff;
}
.node-type-article .frame-rail-1:before {
    background: #0a0a0a
}
.wwe-article__hero--wrapper {
    background: #0a0a0a
}
.node-type-article .pane-node-body .field__item>p, .node-type-article .pane-node-body li:not(.vjs-menu-item):not(.vjs-menu-title) {
    color: #ffffff;
}
.article-content-block .article-content-block--items .article-content-block--item .article-content-block--item--title a {
    color: white;
}
.wwe-match--hero--title {
    color: #fff;
}
.wwe-match--body__section .wwe-match--body__section--body--inner>p {
    color: #fff;
}
.node-type-article .pane-node-body .field__item>p a, .node-type-article .pane-node-body li:not(.vjs-menu-item):not(.vjs-menu-title) a {
    color: #00C0FF;
}
.quick-hits li a {
    color: #fff;
}
.wwe-shows__section--title, .wwe-search-results--title {
    color: #fff;
}
.episode-feed-card--body p {
    color: #ffffff;
}
.episode-feed-card--body a {
    color: #ffffff;
}
.episode-feed-card--assoc-talent a {
    color: #fff;
}
.episode-feed-card--related--text {
    color: #fff;
}
.wwe-episode__byline--dateline {
    color: #ffffff;
}
.episode-feed-card--assoc-talent--title {
    color: #fff;
}
.node-type-episode .wwe-episode--show-title, .node-type-episode .wwe-episode--show-status {
    color: #ffffff;
}
.wwe-episode--header--info, .wwe-match--header--info, .wwe-episode--venue, .wwe-match--venue, .wwe-episode--broadcast, .wwe-match--broadcast {
    background-color: #0a0a0a;
}
.wwe-episode--broadcast--logo svg, .wwe-match--broadcast--logo svg {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
.wwe-episode--header-link, .wwe-match--header-link {
    color: #fff;
}
.show-contextual--items-wrapper .show-contextual__item__copy__text__title {
    color: #fff;
}
.episode-feed-cards__special-preview .episode-feed-card .episode-feed-card--read-more, .episode-feed-cards__special-result .episode-feed-card .episode-feed-card--read-more {
    color: #ffffff;
}
.quick-hits li {
    border-top: 1px solid #272727;
}
.quick-hits li:last-child a {
    border-bottom: 1px solid #272727;
}
.episode-feed-cards {
    border-left: 3px solid #272727;
}
.episode-feed-card:before {
    background-color: #272727;
    box-shadow: 0 0 0 8px #272727;
}
.episode-feed-card--main-col {
    border-right: 1px solid #272727;
}
.episode-feed-card--aside {
    border-left: 1px solid #272727;
}
.wwe-talent__hero-bg-overlay {
    background-color: rgba(0, 0, 0, 0.9);
}
.wwe-talent__hero--label .wwe-championship-title {
    color: #fff;
}
.wwe-talent__hero--wrapper .wwe-talent__hero--info-container {
    border-top: 1px solid #272727;
}
.wwe-talent__hero--wrapper .wwe-talent__hero--info-container {
    border-bottom: 1px solid #272727;
}
.wwe-talent__bio {
    border-right: 1px solid #272727;
}
.wwe-talent__bio-name .pane-title>span, .wwe-talent__bio-name>h3>span, .wwe-talent__latest-news .pane-title>span, .wwe-talent__latest-news>h3>span {
    color: #fff;
}
.wwe-feed-cards--card, .wwe-feed-cards--card.wwe.superstars {
    border-top: 1px solid #272727;
}
.wwe-feed-cards--network-promo>hr, .wwe-feed-cards--network-promo .promo-separator {
    background: linear-gradient(to right, #000000 0%, #000000 25%, #000000 50%, #000000 75%, #000000 100%);
}
.events-upcoming-card--start-date {
    color: #fff;
}
.events-upcoming-card--title, .events-upcoming-card--title a {
    color: #fff;
}
.events-upcoming-card--venue a, .events-upcoming-card--location a {
    color: #fff;
}
.wwe-events-card--link {
    color: #fff;
}
.wwe-events__section--title, .pane-events-search-results .events-search-results--no-results, .pane-events-news-feed-pane .events-search-results--no-results, .wwe-how-to-watch--no-results {
    color: #ffffff;
}
.wwe-events__section--teaser, .wwe-events__section--teaser p {
    color: #ffffff;
}
.wwe-content-toggle__events {
    color: white;
}
.wwe-events-card--link__more-details {
    color: #fff;
}
.wwe-vlanding--fcards {
    background: #0e0e0e;
}
.wwe-vlanding--title {
    background: #272727;
}
.wwe-vlanding--fcards--item--title {
    color: #ffffff;
}
.landing-page--feed-card .card-title {
    color: #fff;
}
.wwe-landing-page--author.wwe-landing-page .landing-page--feed-card {
    border-top: 1px solid #272727;
}
.wwe-content-toggle {
    color: #fff;
}
}
