}

.btn-growth-md, .btn-growth-md:active, .btn-growth-md:hover, .btn-growth-md:link, a.btn-growth-md, a.btn-growth-md:active, a.btn-growth-md:hover, .left-col-list.a.btn-growth-md:link{
 color: #fff!important;
}
#GroupCreationsTabLink,#LibraryTabLink,#PayoutTabLink,#MyCreationsTabLink {
 color:#fff;   
}

.container-footer {
 display:none;   
}
.user-name-container::after {
    content:" ";
}
#navbar-search-input::after {
    content:" the web!";
}
.icon-default-friendstatus, .icon-game, .icon-online, .icon-studio {
background-repeat: no-repeat!important;
    background-size: 100% auto!important;
}
iframe, .rbx-nav-sponsor {
    display:none!important;
}
    
body {
 height: 100vh;   
}
.dialog-input {
 color:#fff!important; 
}
