.btr-game-button {
    border: 0px solid #b8b8b8!important;
    backdrop-filter: blur(3px)!important;
}
#header {
    box-shadow: 0 0 8px 0 rgba(0, 0, 0, .3);
}
.rbx-navbar-search-option, .rbx-navbar-search-anchor, .dropdown-menu, .rbx-navbar-search-option, .rbx-clickable-li, .rbx-navbar-search-option, .rbx-clickable-li, .btr-fastsearch, .rbx-navbar-search, .rbx-navbar-search-option.selected, .dropdown-menu, li {
    background: #ff000000!important;
    backdrop-filter: blur(3px)!important;
}
.dialog-message {
    background-color: #11121494!important;
    border: 0px!important;
}
.rbx-header {
    border-width: 0 0 0px!important;
}
.banner-error {
    display:none !important;
}
body {
    background-image:url("https://th.bing.com/th/id/Rb57255b1d24b01871dc00a3e5bdc6884?rik=aRXKOKFb%2f0EcBw&pid=ImgRaw");
    background-repeat: no-repeat;
    background-size: cover;
}
.input-field {
    background: #ff000000!important;
    backdrop-filter: blur(3px)!important;
    border-color: hsla(0, 0%, 100%, .2);
    color: #bdbebe;
    border-radius: 8px;
}
.people-list, .container-list {
    border-radius: 25px!important;
    box-shadow: 0 0 11px 0 rgba(0, 0, 0, .3);
    width:106%;
    height:1000%;
    backdrop-filter: blur(3px)!important;
    padding:9px;
}
.menu-vertical {
    color: #fff!important;
}
.menu-option {
    color: #fff!important;
}
.menu-option.active {
    box-shadow: inset 4px 0 0 0 #fff;
}
.select-option, option {
    color:#fff!important;
}
.text-label, .font-header-2, .dynamic-ellipsis-item {
    color:#fff!important;
}
.user-account-header {
}
.place-name {
    color:#fff!important;
}
.content {
    background: #ff000000!important;
}
.container-footer {
    z-index:3;
    background: #ff000000!important;
    backdrop-filter: blur(3px)!important;
}
body {
    color:fff!important;
}

