@-moz-document url-prefix("https://web.roblox.com/universes/"), url-prefix("https://web.roblox.com/develop"), url-prefix("https://www.roblox.com/universes/"), url-prefix("https://www.roblox.com/develop") {
.verticaltab a,h2, h2, h1,.a:link,a:visited, a:active,h3, h3 {
 color:#fff!important;
}

td {
     background: #ff000000!important;
    backdrop-filter: blur(3px)!important;   
}
.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited {
    background-image: none!important; 
        background-color:  #0066ff
!important; 
}

.gear-button {
    background:url(https://static.rbxcdn.com/images/BuildPage/btn-gear_sprite_27px.png)!important;
}
 a:hover,a:focus,a:active {
     color:#000!important;           
          }
.verticaltab.selected {
    background: none;
    color:#fff;
}

a.active,a.hover,a.focus,.active-only-checkbox {
    color:#fff!important; 
}
.tab-content, .tab-container+div>div {
    background-color: transparent!important;  
}
#StudioWidget {
 display:none;   
}
#build-page td.menu-area a.tab-item {
    display: block;
    padding: 5px 10px;
    color: white!important;
    font-size: 16px;
}
.info-tool-tip {
 display:none   !important;   
}

a,#build-page, .menu-area, tab-item,a:link, a:visited, a:active,a:link, a:visited, a:active {
     color: white!important;   
}
div {
     background-repeat: repeat-y;
    background-size: contain;
    background-color:black;
}

.active-only-checkbox::after {
  content:" (sorry the background is black... I had a issue with the background that I could not fix.)";
}
body {
       background-repeat: repeat-y;
    background-size: fill;
    background-color:black;
}
}

@-moz-document url-prefix("https://web.roblox.com/universes/"), url-prefix("https://www.roblox.com/universes/") {
body,.form-label {
 color:white!important;     
    
}
body {
     background-image:none;   
}

.headline::after {
       content:" (sorry the background is black... I had a issue with the background that I could not fix.)";
}
.missing-start-place {
 background:black;   
}
}
