css:
/* i really want this to be global */
body {
    background-color:#000!important;
    color:rgb(235,235,235);
}
.navbar > .navbar-inner, .modal-body {
    border-color:transparent!important;
    border-radius:0px!important;
    background:#212121!important;
}
#site-news-forum > article > .forumpost, .block, .course-summaryitem, .breadcrumb, .dropdown-menu, .menu, .card, .card-footer, .course-listitem, .quizattemptsummary > tbody, .lastrow, .info, .formulation, .comment, .ablock, .answer, .que.essay .qtype_essay_response.readonly, .outcome, table.quizreviewsummary td.cell, table.quizreviewsummary, table.quizreviewsummary th.cell, .custom-select, .message-drawer {
    border-color:transparent!important;
    border-radius:5px!important;
    background-color:#212121!important;
    transition: 0.2s linear!important;
}
table#form td.submit, .form-buttons, .path-admin .buttons, #fitem_id_submitbutton, .fp-content-center form + div, div.backup-section + form, #fgroup_id_buttonar, fieldset .hidden {
    background:inherit!important;
}
table#explaincaps tbody > tr:nth-child(2n+1) > td, table#defineroletable tbody > tr:nth-child(2n+1) > td, table.grading-report tbody > tr:nth-child(2n+1) > td, table#listdirectories tbody > tr:nth-child(2n+1) > td, table.rolecaps tbody > tr:nth-child(2n+1) > td, table.userenrolment tbody > tr:nth-child(2n+1) > td, table#form tbody > tr:nth-child(2n+1) > td, form#movecourses table tbody > tr:nth-child(2n+1) > td, #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > td, .forumheaderlist tbody > tr:nth-child(2n+1) > td, table.flexible tbody > tr:nth-child(2n+1) > td, .generaltable tbody > tr:nth-child(2n+1) > td, table#explaincaps tbody > tr:nth-child(2n+1) > th, table#defineroletable tbody > tr:nth-child(2n+1) > th, table.grading-report tbody > tr:nth-child(2n+1) > th, table#listdirectories tbody > tr:nth-child(2n+1) > th, table.rolecaps tbody > tr:nth-child(2n+1) > th, table.userenrolment tbody > tr:nth-child(2n+1) > th, table#form tbody > tr:nth-child(2n+1) > th, form#movecourses table tbody > tr:nth-child(2n+1) > th, #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > th, .forumheaderlist tbody > tr:nth-child(2n+1) > th, table.flexible tbody > tr:nth-child(2n+1) > th, .generaltable tbody > tr:nth-child(2n+1) > th, .add-on, .bg-white, .modal-header, .modal-footer, .fp-navbar  {
    background:#212121!important;
}
.filepicker-filelist, .filemanager-container, .fp-content, .filepicker-filelist, .filemanager-container, .fp-navbar {
    border:none!important;
    border-radius:5px!important;
}
.course-listitem:hover, .qtype_essay_editor > pre, .qtext > pre, .searchinput, input .searchinput, .bg-light, .fp-content, .filepicker-filelist, .filemanager-container {
    background:#151515!important;
}
#searchform_search {
    background:#151515!important;
}
.formulation > .correct, .formulation .correct, .submissiongraded {
    background:#166a2f5e!important;
}
.formulation > .incorrect, .formulation .incorrect {
    background:#ff252547!important;
}
.submissiongraded, .submissionnotgraded {
    color:rgb(235,235,235)!important;
}
.submissionnotgraded {
    background:#0a70ff40!important;
}
.block_myoverview {background:transparent!important;}
.header, h1, h2, h3, h4, h5, h6, .brand, .breadcrumb > li > span > a, .qtext, .qtext > p, .qtype_essay_editor > pre, .qtext > pre, .ablock, .outcome, .custom-select {
    color: rgb(235,235,235)!important;
    text-shadow: none!important;
}
#site-news-forum > article > .forumpost > .maincontent {
    color: rgb(215,215,215)!important;
}
input[type=text]:not(.searchinput) {
    background-color:#212121!important;
    color:rgb(225,225,225);
    border-color:transparent!important;
}
button, .btn, input.form-submit, input[type="button"], input[type="submit"], input[type="reset"], button, input.form-submit, input[type="button"], input[type="submit"], input[type="reset"] {
    background-color:transparent!important;
    background:transparent!important;
    border-radius:5px!important;
    border-color:rgb(225,225,225)!important;
    color:rgb(235,235,235)!important;
    transition: 0.1s linear;
}
button:hover, input.form-submit:hover, input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover, button:focus, input.form-submit:focus, input[type="button"]:focus, input[type="submit"]:focus, input[type="reset"]:focus, .btn:hover, .btn:focus, .btn:active {
    background-color:rgb(225,225,225)!important;
    border-color:rgb(225,225,225)!important;
    color:#212121!important;
    transition: 0.1s linear;
}
.coursemenubtn, .coursemenubtn:hover, .coursemenubtn:active, .coursemenubtn:focus {
    border-color:transparent!important;
    background:transparent!important;
    border:none!important;
    outline:none!important;
}
.navbar .nav li.dropdown.open > .dropdown-toggle, .navbar .nav li.dropdown.active > .dropdown-toggle, .navbar .nav li.dropdown.open.active > .dropdown-toggle, .textmenu, li, .textmenu:hover, .textmenu:focus, .textmenu:active, li:focus, li:hover, li:active, .moodle-actionmenu {
    background:transparent!important;
}
table.flexible th, .generaltable th, table.flexible td, .generaltable td {
    border:none!important;
}
.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus, .dropdown-submenu:hover > a, .dropdown-submenu:focus > a, .menu > li:hover, .jsenabled .usermenu .moodle-actionmenu.show .menu a:hover, .user-enroller-panel .uep-search-results .users tbody tr:hover > td, .user-enroller-panel .uep-search-results .cohorts tbody tr:hover > td, table.grading-report tbody tr:hover > td, .forumheaderlist tbody tr:hover > td, .generaltable tbody tr:hover > td, table.flexible tbody tr:hover > td, .category_subcategories tbody tr:hover > td, table#modules tbody tr:hover > td, table#permissions tbody tr:hover > td, .user-enroller-panel .uep-search-results .users tbody tr:hover > th, .user-enroller-panel .uep-search-results .cohorts tbody tr:hover > th, table.grading-report tbody tr:hover > th, .forumheaderlist tbody tr:hover > th, .generaltable tbody tr:hover > th, table.flexible tbody tr:hover > th, .category_subcategories tbody tr:hover > th, table#modules tbody tr:hover > th, table#permissions tbody tr:hover > th {
    background:#7f47ff5e!important; 
}
a, a:visited, a:focus, a:active {color:#7f47ff!important;text-shadow:none!important;}
.progress .bar {background:#7f47ff!important;background-color:#7f47ff!important;}
.progress {background:#313131!important;}
/* Fixes area */
:focus, .custom-select:focus {outline:none!important;}
::-moz-focus-inner {border:0!important;}
.filler {display:none!important;}
