css:
@-moz-document url-prefix("https://fortnitepolska.pl") {
body {
    background: #000;
    color: #12A0FF
}
.header-bottom a {
    color: #fff;
}
.header-bottom a:hover {
    transition: 0, 4s;
    color: #000;
    background-color: #12A0FF;
}
a {
    color: #12A0FF;
}
a:hover {
    color: #12A0FF;
}
.herald-site-content herald-slide {
    background-color: #000;
    color: #12A0FF;
}
.herald-site-content {
    background-color: #000;
    color: #FFF;
}
a.entry-header {
    color: #FFF;
}
a.entry-header:hover {
    transition: 2s;
    color: #12A0FF;
}
.publisher-anchor-color a {
    color:#12A0FF;
}
.comment__header a {
    color:#12A0FF;
    font-weight: 500;
}
.entry-title {
    color: #12A0FF;
}
.entry-title a {
    color: #FFF;
}
.entry-title a:hover {
    color: #12A0FF;
    transition: 0.8s;
}
h2 {
    color:#12A0FF;
}
h1 {
    color: #FFF;
}
.badge {
    color: #fff;
    background: #12A0FF;
    padding: 1px 3px;
    font-size: 10px;
    line-height: 1.1;
    font-weight: 700;
    border-radius: 3px;
    display: inline-block;
    position: relative;
    top: -1px;
    left: 1px;
}
.dark .badge {
    background: #12A0FF;
}
.zvr-reaction-title {
    font-family:'Open Sans', sans-serif;
    text-align: center;
    display: block;
    padding-bottom: 10px;
    font-size: 26px;
    line-height: 1.35em;
    font-weight: 700;
    color: #12A0FF;
}
#zuzu_viral_reactions ul {
    list-style: none;
    margin: 0;
    padding: 0;
    text-align: center;
    background: #000;
    display: inline-block;
    padding: 10px;
}
#zuzu_viral_reactions li a span {
    font-family:'Open Sans', sans-serif;
    font-size: 14px;
    color: #FFF;
    text-align: center;
    display: block;
    margin: 0 0 30px;
}
#zuzu_viral_reactions em {
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    position: absolute;
    bottom: 0;
    margin-left: -35px;
    left: 50%;
    width: 70px;
    font-family:'Open Sans', sans-serif;
    font-size: 12px;
    line-height: 12px;
    font-style: normal;
    text-align: center;
    padding: 8px;
    text-transform: uppercase;
    background: #12A0FF;
    color: #FFF;
    border: 1px solid #12A0FF;
    box-shadow: 0px 0px 40px #12A0FF;
}
#zuzu_viral_reactions em:hover {
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    position: absolute;
    bottom: 0;
    margin-left: -30px;
    left: 50%;
    width: 60px;
    font-family:'Open Sans', sans-serif;
    font-size: 12px;
    line-height: 12px;
    font-style: normal;
    text-align: center;
    padding: 7px;
    text-transform: uppercase;
    background: #12A0FF;
    color: #FFF;
    border: 1px solid #12A0FF;
    box-shadow: 0px 0px 40px #12A0FF;
    transition: 0.5s;
}
.alert.alert--realtime {
    background: #12A0FF;
}
.mks_button mks_button_small squared {
    background-color: #FFF;
}
.dark .comment__footer .vote-down, .dark .comment__footer a, .dark .comment__header .vote-down, .dark .comment__header a {
    color: #12A0FF;
}
.comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
    color: #12a0FF;
    font-weight: 500;
}
.comment-footer__action {
    color: #12a0FF;
    font-weight: 500;
}
.text {
    color: #FFF;
}
.dark .comment__footer a {
    color: #12A0FF;
}
.realtime-button .indicator {
    transition: width .6s ease-in-out;
    display: inline-block;
    width: 300px!important;
    height: 9px!important;
    border-radius: 5px;
    background: #12A0FF;
    position: absolute!important;
    left: 5px!important;
    top: 2px!important;
}
.herald-sticky-prev a:before, .herald-sticky-next a:before, .herald-comment-action, .meta-tags span, .herald-entry-content .herald-link-pages a {
    background: #12A0FF;
}
.dark .post-actions {
    background: #12A0FF;
}
.publisher-anchor-color a {
    color: #FFFF00 !important;
}
.post-actions {
    background:#12A0FF;
    border-radius: 0 0 2px 2px;
    border-top: solid 2px #FFFF00;
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 36px;
    transition: opacity linear .2s;
}
.btn {
    border: none;
    background: #fff;
    background: #12A0FF;
    display: inline-block;
    padding: 10px 16px;
    color: #fff;
    line-height: 1.1;
    border-radius: 3px;
    font-weight: 500;
    transition: background .2s;
    text-shadow: none;
}
.textarea {
    color: #000;
    background: #12A0FF;
    cursor: text;
    resize: none;
    border: 0;
    display: block;
    padding: 6px 10px 8px;
    margin: 0;
    width: 100%;
    min-height: 44px;
    height: auto;
    line-height: 1.4;
    font-size: 14px;
    font-family:"Helvetica Neue", arial, sans-serif;
    overflow-y: scroll;
    transition: all .15s ease-in-out;
}
.empty-message {
    font-size: 18px;
    font-weight: 500;
    color: #546673;
    text-align: center;
    padding: 75px 15px;
    position: relative;
    background: #12A0FF;
}
element.style {
}
.widget a, .recentcomments a, .widget a:hover, .herald-sticky-next a:hover, .herald-sticky-prev a:hover, .herald-mod-subnav a:hover, .herald-mod-actions a:hover, .herald-slider-controls div:hover, .meta-tags a:hover, .widget_tag_cloud a:hover, .mks_autor_link_wrap a:hover, .mks_themeforest_widget .mks_read_more a:hover, .herald-read-more:hover, .widget .entry-title a:hover, li.herald-mega-menu .sub-menu .entry-title a:hover, .entry-meta-wrapper .meta-item:hover a, .entry-meta-wrapper .meta-item:hover a:before, .entry-meta-wrapper .herald-share:hover>span, .entry-meta-wrapper .herald-author-name:hover, .entry-meta-wrapper .herald-author-twitter:hover, .entry-meta-wrapper .herald-author-twitter:hover:before {
    color: #12A0FF;
}
.widget ul li a, .widget .entry-title a, .herald-author-name, .entry-meta-wrapper .meta-item, .entry-meta-wrapper .meta-item span, .entry-meta-wrapper .meta-item a, .herald-mod-actions a {
    color: #FFF;
}
.herald-sticky-prev a:before, .herald-sticky-next a:before, .herald-comment-action, .meta-tags span, .herald-entry-content .herald-link-pages a {
    background: #12A0FF;
}
body {
    background: #000;
    color: #12A0FF;
}
.dark .comment__footer .vote-down, .dark .comment__footer a, .dark .comment__header .vote-down, .dark .comment__header a {
    color: #12A0FF;
}
.comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
    color: #12a0ff;
    font-weight: 500;
}
element.style {
    overflow: auto;
    overflow-wrap: break-word;
    max-height: 350px;
    background: #333;
}
.textarea {
    color: #FFF;
    cursor: text;
    resize: none;
    border: 0;
    padding: 6px 10px 8px;
    margin: 0;
    width: 100%;
    min-height: 44px;
    height: auto;
    line-height: 1.4;
    font-size: 14px;
    font-family:"Helvetica Neue", arial, sans-serif;
    overflow-y: scroll;
    word-break: break-word;
    transition: all .15s ease-in-out;
}
.dark .post-actions {
    background: #333;
}
}
