@-moz-document domain("drive.google.com") {
/*the whole document —— 整個網頁*/
    /*remove the width limit —— 移除寬度限制*/
        body{
            min-width: 0;
        }
/*left navigation bar —— 左側瀏覽窗格*/
/*[[nav_bar]]*/
/*The "Add" Button —— 「新增」按鈕*/
    /*let it back to the path bar —— 回到地址欄*/
        .a-D-B-x .a-D-B-Lc-j{
            padding:0
        }
    /*change round corner and the size —— 更改圓角&大小*/
        .a-ec-Gd .Kzazxf.x6jRSb{
            border-radius: 0px 0px 15px 0px;
            border:0;
            min-width:unset;
            width:50px;
        }
    /*move the path bar —— 移動地址欄*/
        .a-qc-La:not(.a-qc-La-ee) .a-s-Ba-Ak{
            margin-left:45px;
        }
/*The top bar —— 頂部欄 (icon, search bar, help, setting, apps, user)*/
/*[[top_bar]]*/
/*uploading notification box —— 上傳通知方塊*/
/*[[upload_notify_bar]]*/
/*Tell everyone about Taiwan, ROC (the real China)*/
/*[[ALittleSurprise]]*/
}

