/*remove space —— 移除多餘空間*/
    .gb_3d.gb_le.gb_ce, .gb_wc.gb_Be, .gb_3c.gb_0a.gb_2c, .a-D-B-WErN3d-j{
        padding: 0;
        margin:0
    }
/*use min size & be at the top  —— 最小化&置頂*/
    .a-D-B-WErN3d-j/*normal*/,.a-D-B-WErN3d-j.gc-FnmOde/*when the complementary is open 當側面版開啟*/{
        width: 40px;
        height:48px;
        overflow: hidden;
        position: absolute;
        z-index: 3;
        background-color:#FFF;
        transition:width .2s;
    }
    .a-D-B-WErN3d-j header{
        opacity:0;
        width:100vw;
        transition: opacity .2s ease .3s;
    }
    .a-D-B-WErN3d-j::before{
        content:"⋮";
        margin:0px auto;
        font-size:36px;
        width:100%;
        height:48px;
        position:absolute;
        text-align:center;
        opacity:1;
        transition: opacity .2s ease .3s
    }
    /*use max size while mouse hovering —— 滑鼠懸浮時最大化*/
        .a-D-B-WErN3d-j:hover{
            width: 100vw;
            overflow: unset;
        }
        .a-D-B-WErN3d-j:hover header{
            opacity:1;
            display:inline-block;
            width:100vw
        }
        .a-D-B-WErN3d-j:hover::before{
            opacity:0
        }
        /*increase the area that can be hovered —— 增加懸浮判定區域*/
            .a-D-B-WErN3d-j::after{
                content:"";
                height:64px;
                width:100vw;
                position:absolute;
                top:0;
                left:0;
                z-index: 3;
            }
        /*deeper-search box use the max size 進階搜尋最大化*/
            .h-uj.a-Va-Zb-mb{
                min-width:unset!important;
                width:100vw;
                height:100vh;
                position:fixed;
                top:0!important;
                left:0!important;
            }
            .h-uj.a-Va-Zb-mb .a-Va-Zb-Q-j{
                max-height:calc(100vh - 56px);
                overflow:scroll
            }
            .h-uj.a-Va-Zb-mb .a-Va-Zb-nb-j{
                margin:0
            }
/*search bar use the max size —— 搜尋列最大化*/
    form.gb_af.gb_If.gb_bf{
        width:100vw!important;
        right:0!important;
        max-width:unset;
        position:absolute;
        z-index:4
    } 
    /*increase the area that can be hovered —— 增加懸浮判定區域*/
    form.gb_af.gb_If.gb_bf::after{
        content:"";
        width:100vw;
        height: calc(100vh - 48px);
        position:absolute;
        top:48px;
        z-index:9998;
    }
/*let the top bar and the path bar be in the same line —— 讓頂部欄與地址欄位於同一行*/
    .a-D-B-WErN3d-j, .a-D-B-x{
        display: inline-block;
    }
    .a-D-B-x{
        height:48px;
        width: calc(100vw - 50px);
        left: 44px;
    }
    .a-D-B-x>*{
        height:48px
    }
/*add shadow & change border —— 加上陰影 & 修改邊框*/
    .a-D-B-WErN3d-j{
        box-shadow: -2px 1px 2px 0 rgba(60,64,67,0.302), -3px 1px 3px 1px rgba(60,64,67,0.149);
        border-width:0 1px 0 0;
    }
/*due to some reason, the drive icon will disappear sometimes, this to let it back*/
    .a-D-B-WErN3d-j a.gb_xe.gb_vc.gb_ve .gb_Fa{
        position:static;
        top: 4px;
        background-color:#FFF;
        z-index:5;
        display: inline-block!important
    }

