css:
@-moz-document domain("marketplace.tf") {
body {
    background: #222;
    color: #b9b9b9;
}

.dropdown-menu {
    background-color: #242424;
}

.panel {
    background-color: #242424;
}

.footer {
    background-color: #202020;
    border-top: 1px solid #868686;
}

.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #202020;
}
.panel-info {
    border-color: #393939;
}
.dashboard-earned-funds-available-text {
    color: #ddd;
}
.dashboard-shelf-space-used-text {
    color: #ddd;
}
hr {

    border-top: 1px solid #393939;
}
.dashboard-info-header {

    color: #ddd;
}
.dashboard-held-funds-available-text {

    color: #ddd;
}
.header-decoration {

    background-color: #393939;
}
.app-container {
    background-color: rgb(70, 70, 70);
}
a {
    color: #555;
}
.item-box {
    background-color: #2a2a2a;
}
.item-box-detail {
    background-color: #262626;
}
.form-control {
    color: #b9b9b9;
    background-color: #333;
    border: 1px solid #555;
}
.item-box.is-hovered {
    background-color: #444;
}
.item-box.is-hovered .item-box-detail{
    background-color: #4a4a4a;
}
.panel-info>.panel-heading {
    color: #fff;
    background-color: #2f2f2f;
    border-color: #666;
}
element.style {
    color: rgb(221, 221, 221);
}
.auction-options {
    border-right: 1px solid #393939;
}
.auction-image {
    border-right: 1px solid #393939;
}
.item-title-container {
    border-bottom: 1px solid #393939;
}
table-bordered>thead>tr>th, .table-bordered>thead>tr>th, table-bordered>tbody>tr>th, .table-bordered>tbody>tr>th, table-bordered>tfoot>tr>th, .table-bordered>tfoot>tr>th, table-bordered>thead>tr>td, .table-bordered>thead>tr>td, table-bordered>tbody>tr>td, .table-bordered>tbody>tr>td, table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>td {
    border: 1px solid #393939;
}
a {
    color: #b9b9b9;
}
.dropdown-menu .divider {
    background-color: #393939;
}
.dropdown-menu>li>a {
    color: #b9b9b9;
}
.dashboard-items-panels .panel {
    border-color: #393939;
}
.dashboard-items-panels .panel .panel-heading {
    background-color: transparent;
    border-bottom: 1px solid #393939;
    color: #b9b9b9;
}
.list-group-item {
    background-color: #242424;
    border: 1px solid #393939;
}
.modal-footer {
    border-top: 1px solid #393939;
}
.modal-header {
    border-bottom: 1px solid #393939;
}
.modal-content {
    background-color: #242424;
}
}
