css:
@-moz-document domain("7more7.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 10px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 10px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 10px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background: linear-gradient(180deg, #444, #333)
        }

body, .product .text {
    background: #111;
}
/*-------------- Pizda na invercie --------------*/
#section-counter {
    filter: invert(100%);
}
#section-counter > .container > .pb-3.mb-3.justify-content-center.row > .ftco-animated.fadeInUp.ftco-animate.text-center.heading-section.col-md-12 > h2, div.ftco-animated.fadeInUp.ftco-animate.counter-wrap.justify-content-center.d-flex.col-md-3:nth-of-type(2) > .text-center.block-18 > .text > .number, div.ftco-animated.fadeInUp.ftco-animate.counter-wrap.justify-content-center.d-flex.col-md-3:nth-of-type(1) > .text-center.block-18 > .text > .number {
    color: #000000cf !important;
}
div.ftco-animated.fadeInUp.ftco-animate.counter-wrap.justify-content-center.d-flex.col-md-3:nth-of-type(1) > .text-center.block-18 > .text > span, div.ftco-animated.fadeInUp.ftco-animate.counter-wrap.justify-content-center.d-flex.col-md-3:nth-of-type(2) > .text-center.block-18 > .text > span {
    color: #444444cf;
}
/*-------------- Koniec pizdy na invercie --------------*/
.ftco-navbar-light.scrolled, .bg-light, section:nth-of-type(5) > div, .align-items-center.d-flex.parallax-img, .mouse-icon, section:nth-of-type(2) > div, .form-control, .card-header, .card-body, section > div, option {
    background: #111 !important;
}
nav.nav.nav-pills.nav-fill {
    background: #222 !important;
}
.mouse-icon, .form-control, .card {
    border-color: #222;
}
.table th, .table td, .product-details button, .product-details .form-control {
    border-color: #151515;
}
.ftco-navbar-light .navbar-brand {
    background: url(https://www.7more7.com/images/77c.png) no-repeat center !important;
    display: block !important;
    background-size: 60px !important;
    position: relative;
    right: 80px;
}
img.d-none.d-lg-block {
    visibility: hidden;
}
.ftco-navbar-light .navbar-nav > .nav-item > .nav-link, h1, h2, h3, h4, h5, .h1, .h2, .h3, .h4, .h5, .product .text h3 a, .product .text p.price span.price-sale, .wrap-about .heading-section-bold h2, .wrap-about .heading-section-bold h2 span, .product .text .bottom-area a.add-to-cart, .product .text .bottom-area a, .testimony-wrap .name, .ftco-counter .text strong.number, .ftco-footer .ftco-footer-widget ul li a, .ftco-footer-social li a span, .mouse-wheel, .subscribe-form .form-group input, .form-control, .product-details .price span, .product-details button i {
    color: #eee !important;
}
.ftco-navbar-light .navbar-nav > .nav-item > .nav-link:hover {
    color: #ccc !important;
}
p, .ftco-counter .text span, label, tr:nth-of-type(14) > td, .ftco-animated.fadeInUp.ftco-animate.pl-md-3.product-details.col-lg-6, b, strong, .card-body, .ftco-animated.fadeInUp.ftco-animate.pb-md-5.wrap-about.py-5.col-md-7 > form, body {
    color: #bbb;
}
.heading-section .big {
    color: #aaaaaa03 !important;
}
.insta-icons {
    background: #111111ab;
    backdrop-filter: blur(1px);
}
input[type="submit"] {
    background: linear-gradient(180deg, #222, #111);
    border-color: #222;
    color: #eee;
}
input[type="submit"]:hover {
    background: #00ffffad;
    border-color: #00d5ff;
    color: #000;
}
.alert-info {
    color: #0d5864;
    background-color: #d1ecf19c;
    border-color: #bee5eb;
}
.alert-success {
    color: #155724;
    background-color: #d4edda9c;
    border-color: #c3e6cba6;
}
.alert-success.alert > strong {
    color: #155724;
}
.subscribe-form .form-group .submit:hover {
    border-color: #0000;
}
.hero-wrap .slider-text .bread {
    color: #000 !important;
}
#section-counter > .container {
    background: #0000 !important;
}
.btn-warning:hover {
    color: #bbbbbbcf !important;
}
.product-details .form-group .icon span {
    color: #eee;
}
}
