css:
@-moz-document domain("activision.com"), domain("embed.tagboard.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 5px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 5px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #f4ba37
        }

#login article section .atvi-login-step1, #login article section #console-login .console-login, #login article section .or, #login article section #frmLogin #login-info, #login article section #frmLogin, .blog-aggregation h2 span, .blog-aggregation .blog-txt-container, .blog-aggregation .blog-tile, .community-container .header span, .post, .author-meta-container .author-meta, .blog-post-container[data-game='mw'] .blog-body, .blog-post-info-container .blog-post-info-entry, .blog-top-container .post-feature-container, .blog-bottom-container .post-container, .blog-bottom-container .post-container .post-date, .blog-post-container .blog-body {
    background: #111;
    color: #eee;
}
body {
    background-color: #111;
    color: #eee;
}
#login article section .atvi-login-step1 p, p, #login article section #frmLogin #login-info h6, #login article section .or p, #login article section #frmLogin #login-info .no-login-text, #login article section #frmLogin #login-info #login-need-account, .blog-aggregation .blog-tabs-item:hover, .blog-aggregation .blog-tabs-item.active, .blog-aggregation .blog-btn-secondary, .blog-post-container a, .blog-post-info-container .blog-post-info-entry a, .blog-top-container .blog-top-left h2, .blog-top-container .post-feature-container .post-feature-content .post-header, .blog-top-container .blog-top-right h2, h1, h2, h3, h4, h5, h6, h7, .activision-footer .atvi-locale-selector .locale-link, .activision-footer .atvi-locale-selector a {
    color: #eee;
}
#login article section #frmLogin #login-info .data-row label, #login article section #frmLogin #login-info #login-forgot-password, .blog-aggregation .blog-tabs-item {
    color: #ccc;
}
.author-meta-container .author-meta .author-content > p {
    color: #aaa;
}
.blog-aggregation .blog-btn-secondary {
    border-color: #eee;
}
.blog-post-info-container .blog-post-info-entry, .atvi-blog-footer-container .footer-top, .blog-bottom-container .post-container {
    border-color: #222;
}
.blog-bottom-container .post-container .post-date {
    border-color: #111;
}
#login article section .or p:before, #login article section .or p:after, .blog-aggregation h2:after, .blog-aggregation .blog-tab-underline, .blog-aggregation .blog-divider::before, .blog-aggregation .blog-divider::after, .community-container .header:after {
    background: #333;
}
#login article section #frmLogin #login-info .data-row .input-container input, input {
    background: #111;
    color: #eee;
    border-color: #222;
}
/*Top left corner*/
#login article section #frmLogin #login-info h6 .corners.left:before, #login article section .atvi-login-step1 p .corners.left:before {
    border-left: 1px solid #222;
    border-top: 1px solid #222;
}
/*Bottom left corner*/
#login article section #frmLogin #login-info h6 .corners.left:after, #login article section .atvi-login-step1 p .corners.left:after {
    border-left: 1px solid #222;
    border-bottom: 1px solid #222;
}
/*Top right corner*/
#login article section #frmLogin #login-info h6 .corners.right:before, #login article section .atvi-login-step1 p .corners.right:before {
    border-right: 1px solid #222;
    border-top: 1px solid #222;
}
/*Bottom right corner*/
#login article section #frmLogin #login-info h6 .corners.right:after, #login article section .atvi-login-step1 p .corners.right:after {
    border-right: 1px solid #222;
    border-bottom: 1px solid #222;
}

.blog-aggregation .blog-more::after {
    border-top: 5px solid transparent;
    border-bottom: 5px solid transparent;
    border-left: 5px solid #bbb;
}
.blog-aggregation .blog-btn-secondary span::after {
    filter: invert(100%);
}
.atvi-locale-selector .locale-list {
    background: #111;
    box-shadow: 5px 5px 12px #000;
}
.blog-post-info-container.related .slick-dots li.slick-active button:before, .blog-post-info-container.carousel .slick-dots li.slick-active button:before {
    color: #eee;
}
.blog-post-info-container.related .slick-dots li button:before, .blog-post-info-container.carousel .slick-dots li button:before {
    color: #333;
}
}
