css:
@-moz-document domain("anotepad.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 5px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 5px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #007aff;
        }

body, .containerMain, body.theme-light {
    background: #111;
    color: #eee;
}
.theme-light .footer .stats {
    background: #111;
}
.theme-light #view_content .note_content, .theme-light #view_content .note_content .plaintext, .form-control, div.mce-edit-area, .mce-panel, .mce-panel, .mce-btn button {
    background: linear-gradient(180deg, #222, #111) !important;
    color: #eee;
    border-color: #222;
}
.theme-light .comment-item .time, .theme-light .comment-item .action {
    color: #ccc;
}
.mce-container, .mce-container *, .mce-widget, .mce-widget *, .mce-reset {
    color: #eee !important;
}
.theme-light .containerMain .displayContent {
    background: linear-gradient(180deg, #131313, #111) !important;
    border-color: #222;
    color: #ccc;
}
#edit_textarea_ifr {
    filter: invert(100);
}
.mce-btn {
    border-color: #111 !important;
}
.mce-btn-group:not(:first-child), .mce-btn:active, .mce-btn:hover, .mce-btn:focus, .mce-colorbutton:hover .mce-open, .mce-splitbtn:hover .mce-open {
    border-color: #333;
}
}
