@-moz-document url-prefix("https://www.ozbargain.com.au/") {

/*
* DESIGNED BY Cake#0585 (Discord if any problems)
* THANKS FOR USING
* LET YOUR EYES THANK YOU.
* ALSO THE DEALS. ENJOY THE DEALS. 
* EDIT TO YOUR PLEASING. TRIED TO MAKE IT AS USER-FRIENDLY AS POSSIBLE.
*/

/* ------------------------------=------------------------------ */


                /* BELOW IS USED FOR THE NEWSLETTER PROMPT */
                form#newsletter_form input {
                background-color: #1d1d1d;
                outline: none;
                color: #bbb
                }
                /* ABOVE IS USED FOR THE NEWSLETTER PROMPT */


/* ------------------------------=------------------------------ */


                /* BELOW IS USED FOR THE MENU HYPER-LINKS */
                #header 
                    {
                    background-color: #1d1d1d
                    }
                #menu > li > a 
                    {
                    color: white
                    }

                #menu > li:hover > a, #menu > li.hovered > a 
                    {
                        background-color: #151515;
                    }
                /* ABOVE IS USED FOR THE MENU HYPER-LINKS */


/* ------------------------------=------------------------------ */


                /* BELOW IS USED FOR THE MENU HYPER-LINKS DROPDOWN BAR */
                div.menupopup
                    {
                    color: #606060;
                    font-weight: bolder;
                    border-color: #1d1d1d;
                    background-color: #333
                    }
                /* ABOVE IS USED FOR THE MENU HYPER-LINKS DROPDOWN BAR */


/* ------------------------------=------------------------------ */

                /* BELOW IS DROPDOWN BAR TEXT COLOUR */
                div.menupopup ul.dropdown a 
                    {
                    color: #f4eded
                    }
                /* ABOVE IS DROPDOWN BAR TEXT COLOUR */

                /* BELOW IS HOVER BACKGROUND COLOUR */
                div.menupopup ul.dropdown a:hover 
                    {
                    background-color: #1d1d1d;
                    }
                 /* ABOVE IS HOVER BACKGROUND COLOUR */   

/* ------------------------------=------------------------------ */


                /* BELOW IS USED FOR THE SUB-MENU HYPER-LINKS  */
                 #header2nd ul > li > a 
                    {
                    color: #606060;
                    transition: color 0.5s ease   
                    }
                /* ABOVE IS USED FOR THE SUB-MENU HYPER-LINKS  */


/* ------------------------------=------------------------------ */


                /* BELOW IS COUPON CODE BOX, CHANG-CHING $$ */
                div.couponcode 
                    {
                    background: #101010;
                    text-align: center;
                    border: none;
                    width: 25%;
                    }
                /* ABOVE IS COUPON CODE BOX, CHANG-CHING $$ */


/* ------------------------------=------------------------------ */


                /* BELOW IS ROOT CHANGES (EASIER THAN INDIVUDAL CSS) */
                :root 
                    {
                    --page-bg: #1d1d1d;
                    --page-fg: #979797;
                    --light-bg: #101010;    
                    --menuhl-bg: #1d1d1d;
                    --shade3-bg: #131313;
                    --border-clr: #4d4d4d;
                    --link-fg: #bbb;
                    --link2-fg: #606060;
                    --shadow-bg: none;
                    --voteup-bg: #1d1d1d;
                    --votedown-bg: #1d1d1d;
                    --votedown-fg: #c50000;
                    --meta-fg: #565656;
                    }
                /* ABOVE IS ROOT CHANGES (EASIER THAN INDIVUDAL CSS) */


                /* BELOW MAKES OZBARGIN UTILISE ALL SPACE. REMOVE THIS IF YOU WANT THE OLD DESIGN BACK. */
                div.wrap 
                    {
                    max-width: -webkit-fill-available
                    }
                /* ABOVE MAKES OZBARGIN UTILISE ALL SPACE. REMOVE THIS IF YOU WANT THE OLD DESIGN BACK. */


/* ------------------------------=------------------------------ */


                /* BELOW IS IMAGE BOX COLOUR */
                div.foxshot-container a 
                    {
                    background-color: #202020;
                    border: none
                    }
                /* ABOVE IS IMAGE BOX COLOUR */


/* ------------------------------=------------------------------ */


                /* BELOW IS CONTENT BOX BACKGROUND COLOR */
                div.node-teaser 
                    {
                    background-color: #151515;
                    border-bottom: none;
                    margin: 20px;
                    }
                /* ABOVE IS CONTENT BOX BACKGROUND COLOR */


/* ------------------------------=------------------------------ */


                /* BELOW IS TITLES OF DEALS AND VISITING THEM + NEAT HOVER TRANSITION */
                .title a:hover 
                    {
                    color: #606060;
                    transition: color 0.5s ease
                    }
                .title a:visited 
                    {
                    color: #2675ff;
                    }
                .title a:visited:hover 
                    {
                    color: #606060;
                    transition: color 0.5s ease
                    }
                /* ABOVE IS TITLES OF DEALS AND VISITING THEM + NEAT HOVER TRANSITION */


/* ------------------------------=------------------------------ */


                /* BELOW IS FOR COMMENTS ON DEALS */
                div.comment 
                    {
                    background-color: #101010
                    }
                div.comment-op  
                    {
                    background-color: #000000ad;
                    border-color: #606060;
                    background-image: none
                    }
                /* ABOVE IS FOR COMMENTS ON DEALS */


/* ------------------------------=------------------------------ */


                /* BELOW IS TO REMOVE RELATED STORES / DEALS */
                section.relatedstores 
                    {
                    display: none;
                    }
                h2#relatedstores.section 
                    {
                    display: none;
                    }    
                /* ABOVE IS TO REMOVE RELATED STORES / DEALS */


/* ------------------------------=------------------------------ */


                /* BELOW IS FOOTER SETTINGS */
                footer h3 {
                color: #888;
                text-align: center;
                }
                footer p {
                text-align: center;
                }
                footer ul.links {
                text-align: center !important;
                }
                footer ul.links li {
                text-align: center !important;
                float: none;
                display: inline-block
                }
                ul.follow {
                margin-left: 60px;
                }
                /* BELOW IS FOOTER SETTINGS */


/* ------------------------------=------------------------------ */

                /* BELOW IS TO SCROLLBAR CUSTOMISATION */
                ::-webkit-scrollbar-track
                    {
	                -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	                border-radius: 10px;
	                background-color: #101010;
                    }

                ::-webkit-scrollbar
                    {
	                width: 12px;
	                background-color: #101010;
                    }

                ::-webkit-scrollbar-thumb
                    {
	                border-radius: 10px;
	                -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	                background-color: #606060;
                    }
                /* ABOVE IS TO SCROLLBAR CUSTOMISATION */
}
