@-moz-document domain("www.nitrotype.com")
{
   body
	{
	  background-color: #2d2e2e;
	  overflow-y: scroll;
	  background: none;
	  overflow-x: hidden;
	  position: relative;
	}
	.card--grit
	{
 	 background: #9b00ff7d;
	}

	.news-teaser
	{
  	color: #fff;
	}

