	{
 	 background: url("https://thumbs.gfycat.com/MintyTartHerring-small.gif") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
 	 color: #d3d3d3;
 	 background-size: 100%
	}

	.theme--pDefault .profile-bgHead
	{
	  background: url("https://thumbs.gfycat.com/MintyTartHerring-small.gif") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
 	 background-size: 100%
	}

	.theme--pDefault.is-gold .profile-bg
	{
