    border-color: #222;
}
.mp-header:before {
    background: #222;
}
.btn-gray {
    background: #151515;
    border-color: #222;
    color: #eee;
}
.about-us h1, #categorySlider {
    border-color: #151515;
}
#categorySlider .categorySlider ul li:before {
    background: #151515;
}
.box-big-heading.bbh-blue .bbh-al-detailed>ul>li {
    background-color: #ecf6fb5c !important;
}
