    color: #8fbdd7 !important;
}
.bbh-p-head-inner {
    border-color: #aaaa;
}
.bbh-wrap .bbh-primary-theme-dark .bbh-p-head-inner :nth-child(2) a {
    color: #eee !important;
}
.box-big-heading.bbh-blue .bbh-al-list .bse-cat, .box-big-heading.bbh-blue .box-single-entity .bse-cat>a {
    background: #0000 !important;
    color: #8fbdd7 !important;
}
.box-big-heading.bbh-red .bbh-al-list .bse-cat, .box-big-heading.bbh-red .box-single-entity .bse-cat>a {
    background: #0000 !important;
}
.box-big-heading.bbh-red .bbh-al-detailed>ul>li {
    background-color: #ffbcb954 !important;
}
