    background: #fffbcd30;
}
.bbh-p-head-inner, .bbh-wrap .bbh-primary-theme-dark .bbh-p-head-inner {
    background: #000000cf;
    backdrop-filter: blur(3px);
}
.bbh-wrap .bbh-primary-theme-dark .bbh-p-head-inner {
    border-color: #aaaa;
}
.box-big-heading.bbh-grey .bbh-al-detailed>ul>li {
    background-color: #ededed40 !important;
}
.box-big-heading.bbh-dark-green .bbh-al-detailed>ul>li {
    background: #b6dbc740 !important;
}
.box-big-heading.bbh-light-green .bbh-al-detailed>ul>li {
    background: #daebc840 !important;
}
.box-big-heading.bbh-dark-blue .bbh-al-detailed>ul>li {
    background: #bfcbd740 !important;
}
.box-big-heading.bbh-violet .bbh-al-detailed>ul>li {
    background: #eacfd940 !important;
}
.comm-sort-options-box {
    box-shadow: 2px 2px 10px #000;
}
.commentRow .gRobotoCommentsCount {
    filter: invert(100)
}
}
